    public class Builder : SpGeneric
    {
        private class Champ
        {
            public string Type;
            public string line1;
            public string line2;
            public string Name;
            public bool Indexed;
        }
        public Builder()
            : base()
        {
        }

        Dictionary<string, Champ> dict = new Dictionary<string, Champ>();
        //List<Champ> champs = new List<Champ>();
        private void build(Type type, TextWriter commun)
        {


            string strType = "..\\" + type.Name + ".xml";

           // commun.WriteLine(@"<!-- " + type.Name + " -->");
            List<Champ> champs = new List<Champ>();
            using (TextWriter tw = new StreamWriter(File.OpenWrite(strType)))
            {
                tw.WriteLine("<FieldRefs>");
                tw.WriteLine(" <RemoveFieldRef ID=\"{fa564e0f-0c70-4ab9-b863-0177e6ddd247}\" />");
                foreach (PropertyInfo pInfo in type.GetProperties())
                {
                    var p = new PropInfo(pInfo);
                    if (p.IsPrimary)
                    {
                        var champ = new Champ();
                        if (p.isNumber)
                            champ.Type = "Number";
                        else if (p.isString)
                            champ.Type = "Text";
                        else if (p.isDate)
                            champ.Type = "DateTime";
                        else if (p.isEnum)
                            champ.Type = "Text";

                        var name = this.getName(pInfo);
                        if ("ID" == name)
                            continue;

                        champ.Name = name;
                        var custom = pInfo.CustomType(typeof(SpType));
                        if (custom != null)
                            champ.Type = custom.Type;

                        champ.Indexed = pInfo.HasAttribute(typeof(IndexedAttribute));


                        StringBuilder sb = new StringBuilder();
                        sb.Append("ID=\"{");
                        sb.Append(Guid.NewGuid().ToString());
                        sb.Append("}\" Name=\"");
                        sb.Append(name);
                        sb.Append("\"");


                        // string.Format("ID='{{{0}}}' Name='{1}'", Guid.NewGuid().ToString(), p.Name);
                        champ.line1 = sb.ToString();

                        sb = new StringBuilder();
                        sb.Append(" <Field ");
                        sb.Append(champ.line1);
                        sb.Append(" DisplayName=\"");
                        sb.Append(champ.Name);
                        sb.Append("\" Type=\"");
                        sb.Append(champ.Type);
                        sb.Append("\"");
                        if (champ.Indexed)
                            sb.Append(" Indexed=\"TRUE\" ");
                        sb.Append(" Group=\"Collect Fields\" />");

                        champ.line2 = sb.ToString(); 


                        if (!dict.ContainsKey(name))
                        {
                            dict.Add(name, champ);
                            commun.WriteLine(champ.line2); 
                        }
                        else
                            champ = dict[name];

                        champs.Add(champ); 


                        tw.Write(" <FieldRef ");
                        tw.Write(champ.line1);
                        tw.WriteLine(" />");
                    }
                }



                tw.WriteLine("</FieldRefs>");
                tw.WriteLine(); 
                tw.WriteLine("<Fields>");
                foreach (Champ champ in champs)
                {
                    tw.WriteLine(champ.line2); 
                }
                tw.WriteLine("</Fields>");
            }



        }

        public void build(IEnumerable<Type> types)
        {
            dict.Clear();
            using (TextWriter commun = new StreamWriter(File.OpenWrite(@"..\commun.xml")))
            {
                foreach (Type type in types)
                {
                    build(type, commun);
                }


            }
        }



