using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.IO;
using System.Xml;

namespace Coding
{

    delegate void ActionFile(FileInfo file); 
    public class Coder
    {
        private DirectoryInfo baseDir;
        private DirectoryInfo baseDirCmp; 
        private string output;
        private XmlDocument doc = new XmlDocument();
        private XmlElement current; 

        string[] extensions = { ".cs", ".csproj", ".sln", ".aspx", ".aspx", ".ashx", ".xaml",".js", ".html" }; 
        public Coder(DirectoryInfo baseDir, DirectoryInfo baseDirCmp)
        {
            this.baseDir = baseDir;
            this.baseDirCmp = baseDirCmp; 

            output = baseDir.FullName.Replace(baseDir.Root.FullName,"").Replace("\\", "_") + ".xml";
            doc.CreateXmlDeclaration("1.0", Encoding.UTF8.ToString(), "yes"); 
            var db = doc.CreateElement("db");
            var att = doc.CreateAttribute(Constantes.PATH);
            att.Value = baseDir.FullName;
            db.Attributes.Append(att); 
            current = doc.AppendChild(db) as XmlElement; 
        }

        public void Encode()
        {
            actionDir(baseDirCmp, RecordFileOrg);
            actionDir(baseDir, encode);
            doc.Save(@"..\" +  output); 
        }


        private void actionDir(DirectoryInfo dir, ActionFile actionFile)
        {
            if (dir == null || !dir.Exists)
                return; 
            if (dir.FullName.Contains("\\obj\\"))
                return;
            foreach (FileInfo file in dir.GetFiles())
            {
                if (extensions.Contains<string>(file.Extension))
                {
                    actionFile(file);
                }
            }

            foreach (DirectoryInfo sub in dir.GetDirectories())
                actionDir(sub, actionFile); 
        }

        private Dictionary<string, FileInfo> dictOrg = new Dictionary<string, FileInfo>(); 
        private void RecordFileOrg(FileInfo org)
        {
            var str = org.FullName.Replace(baseDirCmp.FullName, "");
            dictOrg.Add(str, org); 
        }




        private void encode(FileInfo file)
        {
            string content = File.ReadAllText(file.FullName, Encoding.UTF8);
            var key = file.FullName.Replace(baseDir.FullName, "");


            if (dictOrg.ContainsKey(key))
            {
                var fileOrg = dictOrg[key];
                string contentOrg = File.ReadAllText(fileOrg.FullName, Encoding.UTF8);
                if (contentOrg.Equals(content))
                    return; 
            }


            var xf = doc.CreateElement(Constantes.FILE);
            current.AppendChild(xf);
            var path = file.FullName.Replace(baseDir.FullName, "");
            var att = doc.CreateAttribute(Constantes.PATH);
            att.Value = path;
            xf.Attributes.Append(att);

            var xcontent = doc.CreateElement(Constantes.CONTENT);
            xf.AppendChild(xcontent);

            var cdata = doc.CreateCDataSection(content);
            xcontent.AppendChild(cdata); 
           // xcontent.InnerText = content ; 
        }
    }
}

    using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Coding
{
    class Constantes
    {
        public const string FILE = "file";
        public const string PATH = "path";
        public const string CONTENT = "content"; 
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.IO;
using System.Xml;

namespace Coding
{
    public class Decoder
    {
        string file;
        DirectoryInfo dir; 
        public Decoder(string file)
        {
            this.file = file;
            var c = new DirectoryInfo(Environment.CurrentDirectory);
            c = c.Parent;
            dir = new DirectoryInfo(c.FullName + Path.DirectorySeparatorChar + "Data");
            if (!dir.Exists)
                dir.Create(); 

        }

        public void Decode()
        {
            var doc = new XmlDocument();
            doc.Load(file);
            var root = doc.DocumentElement;
            var att = root.GetAttribute(Constantes.PATH);

            var dirDepot = new DirectoryInfo(att);
            dir = new DirectoryInfo(dir.FullName + Path.DirectorySeparatorChar + dirDepot.Name);

            if (!dir.Exists)
                dir.Create(); 

            foreach (XmlElement xF in root.ChildNodes)
                decode(xF); 
        }

        private void decode(XmlElement xf)
        {
            var att = xf.GetAttribute(Constantes.PATH);
            var current = dir;
            var tab = att.Split(Path.DirectorySeparatorChar);
            int count = tab.GetLength(0);
           for (int i=0; i<count ; i++)
            {
               string str = tab[i];
               if (string.IsNullOrEmpty(str))
                   continue;
               if (i == count - 1)
               {
                   string content = xf.ChildNodes[0].ChildNodes[0].Value;
                   File.WriteAllText(current.FullName + Path.DirectorySeparatorChar + str, content, Encoding.UTF8);
               }
               else
               {
                   current = new DirectoryInfo(current.FullName + Path.DirectorySeparatorChar + str);
                   if (!current.Exists)
                       current.Create(); 
               }
            }


        }
    }
}

