namespace pdb.player.ViewModel.Commande.PlayList
{
    class AddToList : PlayListCommande
    {
        private IIhm ihm;
        public AddToList(ISelectedTracks provider, IIhm ihm)
            : base(provider)
        {
            this.ihm = ihm;
        }
        public override bool CanExecute(object parameter)
        {
            return base.CanExecute(parameter);
        }


        public override void Execute(object parameter)
        {
            PlayListViewModel org = null;
            if (provider is ItrackProvider)
            {
                org = (provider as ItrackProvider).PlayList;
            }

            var tracks = Tracks;
            int count = tracks.Count;
            if (count == 0)
                return;
            string title = "";
            if (count == 1)
                title = string.Format("ajouter {0} √† la liste...", tracks[0].Name);
            else
            {
                string s = "s";
                if (count == 2)
                    s = "";
                title = string.Format("ajouter {0} et {1} autre{2} √©l√©ment{2} √† la liste...", tracks[0].Name, count - 1, s);
            }
            PlayListViewModel pl = ihm.selectPlayList(title);
            if (pl == null)
                return;

            foreach (TrackListViewModel track in tracks)
            {
                var p = track.PieceGen;
                if (p.Virtual)
                    p = p.Master;
                pl.AddTrack(p, true);
            }

            App.buildList();
            if (org != null)
                org.IsSelected = true;
            //       PlayListViewModel.SelectedPlayList = org; 
        }
    }
}

namespace pdb.player.ViewModel.Commande.PlayList
{
    class CheckCmd : PlayListCommande
    {
        public CheckCmd(ISelectedTracks provider) : base(provider) { }
        public override bool CanExecute(object parameter)
        {
            return base.CanExecute(parameter) && Tracks.Exists(t=>!t.Enabled); 
        }


        public override void Execute(object parameter)
        {
            foreach (TrackListViewModel track in Tracks)
                track.Enabled = true; 
        }
    }

    class UnCheckCmd : PlayListCommande
    {
        public UnCheckCmd(ISelectedTracks provider) : base(provider) { }
        public override bool CanExecute(object parameter)
        {
            return base.CanExecute(parameter) && Tracks.Exists(t => t.Enabled);
        }

        public override void Execute(object parameter)
        {
            foreach (TrackListViewModel track in Tracks)
                track.Enabled = false;
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.player.ViewModel.Commande.PlayList
{
    class DbDeleteLastClassement : PlayListCommande
    {
        public DbDeleteLastClassement(ISelectedTracks provider) : base(provider) { }
        public override void Execute(object parameter)
        {
            var tracks = Tracks;
            string blala = "";
            int count = tracks.Count;
            if (count <= 1)           
                blala = "du morceau " + tracks[0];
            else
                blala = "des " + count + " morceaux";

            bool _confirm = App.Instance.Confirm(string.Format("Voulez-vous supprimer le dernier classement {0} ?", blala), "Abandon classement");

            if (!_confirm)
                return;

            foreach (TrackListViewModel t in tracks)
            {
                var db = t.Piece;
                db.Classement.removeLastVersion(); 
            }

            App.go(true); 
        }
    }
}

namespace pdb.player.ViewModel.Commande.PlayList
{
    class DbDeleteLastRead : PlayListCommande
    {
        public DbDeleteLastRead(ISelectedTracks provider) : base(provider) { }
        public override void Execute(object parameter)
        {
            var tracks = Tracks;
            string blala = "";
            int count = tracks.Count;
            if (count <= 1)
                blala = "du morceau " + tracks[0];
            else
                blala = "des " + count + " morceaux";

            bool _confirm = App.Instance.Confirm(string.Format("Voulez-vous supprimer la derni√®re √©coute {0} ?", blala), "Suppression stats √©coute");

            if (!_confirm)
                return;

            foreach (TrackListViewModel t in tracks)
            {
                var db = t.Piece;
                db.ListDates.removeLast();
            }

            App.go(); 
        }
    }
}

using System.Collections.Generic;

namespace pdb.player.ViewModel.Commande.PlayList
{
    abstract class PlayListCommande : CommandeBase
    {
        protected ISelectedTracks provider;
        //public static ItrackProvider Provider { set { provider = value; } }
        public PlayListCommande(ISelectedTracks provider)
        {
            this.provider = provider; 
        }
        protected List<TrackListViewModel> Tracks
        {
            get
            {
                if (provider == null)
                    return null;
                return provider.SelectedTracks;
            }
        }



        public override bool CanExecute(object parameter)
        {
            var tracks = Tracks;
            return tracks != null && tracks.Count > 0; 
        }
    }
}

