using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;

namespace pdb.obj
{
    public interface IItem
    {
        int ID { get; set; }
    }
    public class FolderBase : IItem
    {
        protected String name = "";
        protected int id;
        protected PlayList folder;

        public FolderBase(String name)
        {
            this.name = name;
        }
        public FolderBase(String name, PlayList folder)
        {
            this.name = name;
            this.folder = folder;
        }
        public string getPath()
        {
            if (folder == null) return name;
            return folder.getPath() + SEP + name;
        }

        public string getPath(PlayList listes, PlayList genres)
        {
            if (folder == null) return name;
            if (folder == genres)
                return name;
            if (folder == listes)
                return name;
            return folder.getPath(listes, genres) + SEP + name;
        }
        public int ID { get { return id; } set { id = value; } }

        public string Name { get { return name; } set { name = value; } }


        public bool isSubList(string a_name)
        {
            if (name == a_name)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(a_name);
        }

        public override string ToString()
        {
            return getPath(); 
        }


        public const char SEP = '\\';

    }
    public class PlayList : FolderBase
    {
      //  private PlayList folder;
        private List<IItem> items = new List<IItem>();
        private List<PlayList> lists = new List<PlayList>();


        public PlayList(string name)
            : base(name)
        {
        }

        public PlayList(string name, PlayList parent)
            : base(name, parent)
        {
            if (parent != null)
                parent.lists.Add(this); 
        }
        public void setFolder(PlayList folder)
        {
            this.folder = folder;
        }

        public void DeleteBase()
        {
            items.Clear();
        }

        public void Delete()
        {
            foreach (PlayList pl in new List<PlayList>(lists))
            {
                pl.Delete();
            }
            lists.Clear(); 
            items.Clear();
            if (folder != null)
                folder.lists.Remove(this); 

        }
        public List<IItem> Items { get { return items; } }

        public void add(PlayList pl)
        {
            lists.Add(pl);
            pl.setFolder(this);
        }

        public void add(IItem item)
        {
            items.Add(item);
        }

        public new bool isSubList(string a_name)
        {
            if (name == a_name)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(a_name);
        }

        public bool isSubList(PlayList other)
        {
            if (other == this)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(other);
        }



        public virtual void DeleteChilds()
        {
            lock (this)
            {
                try
                {
                    this.DeleteChilds(); 

                    foreach (PlayList pl in new List<PlayList>(lists))
                    {
                        pl.Delete();
                    }

                }
                catch (Exception e)
                {
                    misc.log(e.ToString());
                    throw;
                }
            }

        }



        public List<PlayList> PlayLists { get { return lists; } }


    }
}
