                            }
                        }
                        for (int i = 0; i < 6; i++)
                        {
                            sub = list.FindAll(t => t.Rating == 20 * i);
                            if (sub.Count == 0)
                                continue;

                            Console.WriteLine(name + "level " + i + " " + sub.Count);
                            foreach (TrackInfoItunes track in sub)
