                                foreach (TrackInfoItunes track in sub)
                                {
                                    Console.WriteLine("traiter? (o->oui, n->non f->fini) " + track + " " + track.Location + " durée:" + track.Duration.TotalMinutes.ToString("0.#") + " min");
