        private long _size = -1;
        public long Size
        {
            get
            {
                if (_size < 0)
                {
                    var f = FileRegister.Get(Location);
                    var exist = f.Exists;
                    if (exist == null)
                        return -1; 
                    else if (true ==  exist)
                        _size = f.Length;
                    else
                        _size = 0;
                }
                return _size;
            }
        }

