    /// <summary>
    /// Implémentation des paramètres de sélection
    /// </summary>
    class TrackSelector : ITrackSelector
    {
        public TrackSelector(SelectParamBase p)
        {
            this.p = p;
        }
        private SelectParamBase p;
        private int nbOu;
        private int nbAnd;

        public SelectParamBase Param { get { return p; } }

        public bool select(ISelectable t)
        {
            if (nbAnd >= p.nb)
                return false;

            if (t.Rating < 20 * p.level)
                return false;

            if (!string.IsNullOrEmpty(p.except))
            {

                if (!string.IsNullOrEmpty(t.Album))
                {
                    var album = t.Album.ToLower();
                    var palbum = p.except.ToLower();
                    if (!string.IsNullOrEmpty(palbum))
                    {
                        if (album.Contains(palbum))
                            return false;
                        if (palbum.Contains(album))
                            return false;
                    }
                }
            }



            bool and = checkAnd(t);
            bool or = false;
            // si rejeté on teste ou
            if (!and && nbAnd < p.nb)
            {
                or = checkOr(t);
            }

            return or || and;



        }


        private bool checkOr(ISelectable t)
        {


            if (nbOu < p.nbOu)
            {
                var duree = t.Duree.TotalMinute;

                if (duree >= p.ouDureeMin || duree < p.ouDureeMax)
                {
                    nbOu++;
                    return true;
                }
                else
                {
                    var b = CodeEvaluator.Check(t, p.or);
                    if (b.HasValue && b.Value)
                    {
                        nbOu++;
                        return true;
                    }
                }
            }
            return false;
        }

        //private static double getLissage(double d,int index, List<double> tab)
        //{
        //    int count = tab == null ? -1 : tab.Count;
        //    if (count <= 0)
        //        throw new ArgumentException("tab.Count=" + count);

        //}

        public bool selectSimple(ISelectable t)
        {
            if (t.Album == "BIENVENUE AU MOYEN AGE")
            {
            }
            if (t.Actu != p.actu && p.actu != null)
                return false;
            if (t.Info != p.info && p.info != null)
                return false;
            if (t.Courant != p.courant && p.courant != null)
                return false;
            if (t.Recent != p.recent && p.recent != null)
                return false;
            if (t.UnAn != p.unAn && p.unAn != null)
                return false;
            if (t.Open != p.open && p.open != null)
                return false;
            if (t.RareEffect != p.rare && p.rare != null)
                return false;
            if ((int)t.Specif < p.specMin)
                return false;
            if ((int)t.Specif > p.specMax)
                return false;
            if (p.rating != null && p.rating.Value != t.Rating)
                return false;

            var duree = t.Duree.TotalMinute;
            if (duree < p.dureeMin)
                return false;
            if (duree > p.dureeMax)
                return false;
            if (p.indexDuree >= 0)
            {
                var indexDuree = -1;
                //calcul de la position sur l'échelle des durées. 
                int count = p.durees.Count;
                if (duree >= p.durees[count - 1])
                    indexDuree = count;
                else
                {
                    for (int i = 0; i < count; i++)
                    {
                        var d = p.durees[i];
                        if (duree <= d)
                        {
                            indexDuree = i;
                            break;
                        }
                    }
                }
                if (indexDuree != p.indexDuree)
                    return false;

            }



            // a placer en dernier
            if (p.indexRetard >= 0 || p.retardMin > 0 || p.retardMax > 0)
            {
                //DateTime date0 = Conf.Date0; 
                //// if (t.Pub >= date0)
                ////     return false; 
                //DateTime date0 = ContexteBuilder.Instance.Debut;
                //if (p.date0Min && date0 > Conf.Date0)
                //    date0 = Conf.Date0;
                //// if (t.Pub >= date1)
                ////     return false; 
                ////if (date1 > DateTime.MinValue && date1 < date0)
                ////    date0 = date1; 

                //double retard = (date0 - t.Pub.Date).TotalDays;
                double retard = ContexteBuilder.Instance.getRetard(t, p.date0Min); 

                if (retard >= p.retardMin && p.retardMin > 0)
                    return true;
                else if (retard <= p.retardMax && p.retardMax > 0)
                    return true;
                else
                {

                    var indexRetard = -1;

                    if (retard <= 0)
                        indexRetard = 0;
                    else
                    {
                        //calcul de la position sur l'échelle des durées. 
                        int count = p.retards.Count;
                        if (retard >= p.retards[count - 1])
                            indexRetard = count;
                        else
                        {
                            for (int i = 0; i < count; i++)
                            {
                                var d = p.retards[i];
                                if (retard <= d)
                                {
                                    indexRetard = i;
                                    break;
                                }
                            }
                        }
                    }
                    if (indexRetard == p.indexRetard)
                        return true;
                    else
                        return false;
                }

            }

            return true;

        }

        private bool checkAnd(ISelectable t)
        {
            if (nbAnd >= p.nb)
                return false;

            if (!selectSimple(t))
                return false;
            var b = CodeEvaluator.Check(t, p.and);
            bool ret = true;
            if (b.HasValue)
                ret = b.Value;

            if (ret)
                nbAnd++;

            return ret;
        }
    }
}

