    class NoteBuilder
    {
        private ListNote[] depot;
        private Note conf;
        private List<TrackSelector> selectors = new List<TrackSelector>();
        private Logger logger = Logger.getLogger("Notes", false);
        private Dictionary<int, TrackNote> dict = new Dictionary<int, TrackNote>();
        public Note Note { get { return conf; } }
        public NoteBuilder(Note conf)
        {
            this.conf = conf;
            foreach (SelectParamBase p in conf)
                selectors.Add(new TrackSelector(p));
            depot = new ListNote[conf.max - conf.min + 1];
            for (int i = 0; i <= conf.max - conf.min; i++)
            {
                depot[i] = new ListNote(i + conf.min);
            }
          //  conf.NoteBuilder = this; 
        }

        public void build(List<TrackInfoItunes> list)
        {
            foreach (TrackInfoItunes t in list)
                build(t);



        }


        public void display()
        {
            for (int i = conf.max; i >= 0; i--)
            {
                try
                {
                    ListNote listNote = depot[i];
                    logger.log(listNote.Note + ":");
                    foreach (TrackNote tn in listNote.list)
                    {
                        //if (tn.track.Selected)
                        //{
                        tn.log(logger);
                        //logger.log("total:" + tn.total + " " + tn.track.ToString());
                        //foreach (NoteItem noteItem in tn.noteItems)
                        //    logger.logNoDate(noteItem.desc);
                        // }
                    }
                }

                catch
                {
                }
            }
        }

        private void build(TrackInfoItunes t)
        {
            if (dict.ContainsKey(t.DbId))
                return;
            double note = 0;
            var tt = new TrackNote(t);

            if (t.TrackAlbum.Album == "LA FACE CACHEE DU GLOBE PAR NABILA HAMEL")
            {
            }
            else if (t.TrackAlbum.Album == "Les enjeux internationaux")
            {
            }
            else if (t.TrackAlbum.Album == "RFI - Une semaine d'actualité")
            {
            }

            TrackSelector voisin = null;
            int count = selectors.Count;
            for (int i = 0; i < count; i++)
            {
                var ts = selectors[i];

                if (ts.selectSimple(t))
                {
                    var noteItem = ts.Param as NoteItem;
                    var noteItemValue = new NoteItemValue(noteItem);
                    tt.noteItems.Add(noteItemValue);
                    var index = noteItem.indexDuree;
                    if (index >= 0 && index < noteItem.durees.Count)
                    {
                        var duree = t.Duree.TotalMinute;
                        var durees = noteItem.durees;
                        if (index > 0 && duree != durees[index])
                        {
                            var v = noteItem.v;
                            voisin = null;
                            int iVoisin = i + 1;
                            int indexV = -1;
                            // on va chercher le suivant
                            if (iVoisin < count)
                            {
                                voisin = selectors[iVoisin];
                                indexV = voisin.Param.indexDuree;
                                if (indexV < 0 || indexV > index)
                                    voisin = null;
                            }
                            if (voisin == null)
                            {
                                // on cherche le précédent
                                iVoisin = i - 1;
                                if (iVoisin >= 0)
                                {
                                    voisin = selectors[iVoisin];
                                    indexV = voisin.Param.indexDuree;
                                    if (indexV < 0 || indexV > index)
                                        voisin = null;
                                }
                            }
                            if (voisin != null)
                            {
                                var noteItemV = voisin.Param as NoteItem;
                                var vv = noteItemV.v;

                                //<e v="0,7" indexDuree="5" />	<!-- 4,5 -->
                                //<e v="1,3" indexDuree="4" />	<!-- 4 -->
                                // pour 4,4
                                // deltaduree = 0.5
                                // pente= 1.2
                                // delra0= -0.1
                                //val = 0.7+1.2*0.1

                                double deltaDuree = durees[index] - durees[indexV];
                                double pente = (vv - v) / deltaDuree;
                                double delta0 = durees[index] - duree;
                                double val = v + pente * delta0;
                                noteItemValue.Value = val;
                            }
                            else
                            {
                            }
                        }
                    }

                    index = noteItem.indexRetard;
                    if (index >= 0 && index < noteItem.retards.Count)
                    {
                        //DateTime date0 = ContexteBuilder.Instance.Debut;
                        //if (noteItem.date0Min && date0 < Conf.Date0)
                        //    date0 = Conf.Date0;
                        //// if (t.Pub >= date1)
                        ////     return false; 
                        ////if (date1 > DateTime.MinValue && date1 < date0)
                        ////    date0 = date1; 

                        //double retard = (date0 - t.Pub.Date).TotalDays;

                        double retard = ContexteBuilder.Instance.getRetard(t, noteItem.date0Min); 

                        var retards = noteItem.retards;
                        if (index > 0 && retard != retards[index])
                        {
                            var v = noteItem.v;
                            voisin = null;
                            int iVoisin = i + 1;
                            int indexV = -1;
                            // on va chercher le suivant
                            if (iVoisin < count)
                            {
                                voisin = selectors[iVoisin];
                                indexV = voisin.Param.indexRetard;
                                if (indexV < 0 || indexV > index)
                                    voisin = null;
                            }
                            if (voisin == null)
                            {
                                // on cherche le précédent
                                iVoisin = i - 1;
                                if (iVoisin >= 0)
                                {
                                    voisin = selectors[iVoisin];
                                    indexV = voisin.Param.indexRetard;
                                    if (indexV < 0 || indexV > index)
                                        voisin = null;
                                }
                            }
                            if (voisin != null)
                            {
                                var noteItemV = voisin.Param as NoteItem;
                                var vv = noteItemV.v;

                                //<e v="0,7" indexDuree="5" />	<!-- 4,5 -->
                                //<e v="1,3" indexDuree="4" />	<!-- 4 -->
                                // pour 4,4
                                // deltaduree = 0.5
                                // pente= 1.2
                                // delra0= -0.1
                                //val = 0.7+1.2*0.1

                                double deltaDuree = retards[index] - retards[indexV];
                                double pente = (vv - v) / deltaDuree;
                                double delta0 = retards[index] - retard;
                                double val = v + pente * delta0;
                                noteItemValue.Value = val;
                            }
                            else
                            {
                            }
                        }
                    }
                   // tt.total += noteItemValue.Value;
                    note += noteItemValue.Value;
                }
            }

            note += conf.zero;
            note += t.TrackAlbum.Note;
            tt.total = note;
            tt.tri = note; 

            if (note < conf.min)
                note = conf.min;
            if (note > conf.max)
                note = conf.max;
            if (conf.triBorne)
                tt.tri = note; 

            int noteF = (int)System.Math.Round(note, 0, MidpointRounding.AwayFromZero);
            if (conf.triEntier)
                tt.tri = noteF; 
            ListNote d = null;
            d = depot[noteF - conf.min];
            d.Add(tt);
            dict.Add(t.DbId, tt);
            t.Note = tt.total; 
        }

        public IEnumerable<ListNote> Depot { get { return depot; } }

        public void Sort(List<TrackInfoItunes> list, bool histo)
        {
            list.Sort(new TrackComparer(this, histo));

        }
        private class TrackComparer : IComparer<TrackInfoItunes>
        {
            private NoteBuilder noteBuilder;
            private bool histo;

            public TrackComparer(NoteBuilder noteBuilder, bool histo)
            {
                this.noteBuilder = noteBuilder;
                this.histo = histo;
            }
            public int Compare(TrackInfoItunes x, TrackInfoItunes y)
            {
                if (x == y)
                    return 0; 
                var xt = noteBuilder.dict[x.DbId];
                var yt = noteBuilder.dict[y.DbId];
                var cmp = xt.tri.CompareTo(yt.tri);
                if (cmp != 0)
                    return -cmp;
                cmp = x.Pub.CompareTo(y.Pub);
                if (histo)
                    return cmp;
                double retardx = ContexteBuilder.Instance.getRetard(x, noteBuilder.conf.date0Min);
                if (retardx >= noteBuilder.conf.retardHisto)
                {
                    double retardy = ContexteBuilder.Instance.getRetard(x, noteBuilder.conf.date0Min);
                    if (retardx >= noteBuilder.conf.retardHisto)
                        return cmp;
                    else
                    {
                    }
                }
                return -cmp;
            }
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Xml;
using pdb.util;
using System.Reflection;

namespace pdb.podcast.Tuning
{
    public class Listes : IEnumerable<SelectParamBase>
    {
        private List<SelectParamBase> list = new List<SelectParamBase>();
        public Listes()
        {
        }

        public Listes(XmlElement node)
        {
            if (node == null)
                return;
            foreach (var sub in node.ChildNodes)
            {
                if (sub is XmlElement)
                {
                    var xsub = sub as XmlElement;
                    if (xsub.Name == "l")
                        list.Add(new SelectParamBase(node,xsub)); // (xsub));
                }
            }
        }
        public IEnumerator<SelectParamBase> GetEnumerator()
        {
            return list.GetEnumerator();
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return list.GetEnumerator();
        }
    }

    public class SelectParamBase : TuningBase
    {

        /// <summary>
        /// Nombre d'éléments extraits
        /// </summary>
        public int nb = int.MaxValue;

        public string name;
        public string path;
        /// <summary>
        /// Niveau
        /// </summary>
        public int level;

        public int? rating;


        public bool? info;
        public bool? rare;
        public bool? actu;
        public bool? courant;

        public bool? recent;
        public bool? open;
        public bool? unAn;

        public int specMin;
        public int specMax = int.MaxValue;
        public double dureeMin;
        public double dureeMax = int.MaxValue;
        public double ouDureeMin = int.MaxValue;
        public double ouDureeMax = 0;
        public string except = "";
        public int nbOu = 5;
        public string or;
        public string and;
        /// <summary>
        /// Echelle des durées du plus petit au plus grand
        /// </summary>
        public List<double> durees = new List<double>();

        /// <summary>
        /// Echelle des retard 
        /// </summary>
        public List<double> retards = new List<double>();

        public int indexDuree = -1;
        public int indexRetard = -1;

        public double retardMin = -1;
        public double retardMax = -1; 
        public bool date0Min; 


        public SelectParamBase()
            : base()
        {
        }

        public SelectParamBase(XmlElement node) :
            this(null, node)
        {
        }

        //public SelectParamBase(XmlElement node)
        //    : this(null, node)
        //{
        //}

        public SelectParamBase(XmlElement parent, XmlElement node)
            : base(parent, node)
        {
            buildi("nb", v => nb = v);
            builds("name", v => name = v);
            builds("path", v => path = v);
            buildi("level", v => level = v);

            buildbn("info", v => info = v);
            buildbn("rare", v => rare = v);
            buildbn("actu", v => actu = v);
            buildbn("recent", v => recent = v);
            buildbn("courant", v => courant = v);
            buildbn("open", v => open = v);
            buildbn("unAn", v => unAn = v);

            buildi("specMin", v => specMin = v);
            buildi("specMax", v => specMax = v);

            buildd("dureeMin", v => dureeMin = v);
            buildd("dureeMax", v => dureeMax = v);

            buildd("ouDureeMin", v => ouDureeMin = v);
            buildd("ouDureeMax", v => ouDureeMax = v);

            builds("except", v => except = v);

            buildi("nbOu", v => nbOu = v);

            builds("or", v => or = v);
            builds("and", v => and = v);

            buildList("durees", durees);            
            buildi("indexDuree", v => indexDuree = v);
            buildList("retards", retards);
            buildi("indexRetard", v => indexRetard = v);
            buildb("date0Min", v => date0Min = v);
            buildin("rating", v => rating = v);
            buildd("retardMin",v=> retardMin = v);
            buildd("retardMax", v => retardMax = v); 

        }



        public override string ToString()
        {
            var sb = new pdb.util.StringBuilder();
            var t = this.GetType();
            var fs = t.GetFields();
            foreach (FieldInfo f in fs)
            {
                var v = f.GetValue(this);
                if (v == null)
                    continue;
                if (int.MaxValue.Equals(v))
                    continue;
                if (v is int)
                {
                    var i = (int)v;
                    if (i == 0)
                        continue;
                    if (i == int.MaxValue)
                        continue; 
                }

                if (v is double)
                {
                    var d = (double)v;
                    if (d == 0)
                        continue;
                    if (d == int.MaxValue)
                        continue; 
                }

                sb.AppendLine(" ");
                if (v is bool?)
                {
                    var b = (bool?)v;
                    if (true == b)
                        sb.Append(f.Name);
                    else
                    {
                        sb.Append("!");
                        sb.Append(f.Name);
                    }
                }
                else
                {

                    sb.Append(f.Name);
                    sb.Append("=");
                    sb.Append(v);
                }
            }

            return sb.ToString();

        }
    }
}

