    public class Note : SelectParamBase, IEnumerable<NoteItem>
    {
        private List<NoteItem> items = new List<NoteItem>();
        /// <summary>
        /// Format du chemin des listes
        /// </summary>
        public string path;
        public string dir; 
        public int min = 0;
        public int max = 20;
        /// <summary>
        ///  Format du chemin des listes ne contenant que le premier item par niveau
        /// </summary>
        public string single;
        /// <summary>
        /// Format du chemin des listes ne contenant que le premier item d'un album - en admettant qu'on lise les items du niveau le plus haut au plus bas
        /// </summary>
        public string unique; 
        public bool histo;
        public double zero;
        /// <summary>
        /// on utilise les bornes pour lisser les valeurs pour le tri
        /// </summary>
        public bool triBorne;

        /// <summary>
        /// On utilise les valeurs du depot pour trier
        /// </summary>
        public bool triEntier; 
        //private NoteBuilder noteBuilder;
        //internal NoteBuilder NoteBuilder { set { noteBuilder = value; } get { return noteBuilder; } }

        /// <summary>
        /// Retard à partir duquel on tri suivant l'ordre chrono
        /// </summary>
        public double retardHisto = 30; 
        public Note()
        {
        }

        public Note(XmlElement node): base(node)
        {
            if (node == null)
                return;
            builds("dir", v => dir = v);
            builds("path", v => path = v);
            buildi("min", v => min = v);
            buildi("max", v => max = v);
            builds("single", v => single = v);
            builds("unique", v => unique = v);      
            buildb("histo", v => histo = v);
            buildd("zero", v => zero = v);
            buildb("triBorne", v => triBorne = v);
            buildb("triEntier", v => triEntier = v);
            buildd("retardHisto", v => retardHisto = v); 
            items.Clear();
            foreach (XmlNode xitem in node.ChildNodes)
            {
                if (xitem is XmlElement)
                {
                    var item = new NoteItem(node, xitem as XmlElement);
                    items.Add(item);
                }
            }
        }

        public IEnumerator<NoteItem> GetEnumerator()
        {
            return items.GetEnumerator(); 
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return items.GetEnumerator(); 
        }
    }

    public class NoteItem : SelectParamBase
    {
        public double v;
        public string desc; 
        public NoteItem(XmlElement parent, XmlElement node)
            : base(parent, node)
        {
            this.desc = ((XmlElement)node).OuterXml; 
            buildd("v", _v => v = _v);
        }
        public NoteItem()
            : base()
        {
        }
    }
