                        {
                            cnext.UseCompact = false;
                            return A_REFAIRE_CAUSE_COMPACT;
                        }
                    }
#if NO_COEFF
                    if (!cnext.NoCoeff && !cnext.Empty)
                    {
                        if (cnext.Piece.LastClassementRecent)
                        {
                            cnext.NoCoeff = true;
                            return A_REFAIRE_CAUSE_COEFF;
                        }
                    }
#endif
