        //decimal Cumul { get; }
        obj.Classement Classement { get; }
        //obj.Classement Classement2 { get; }
        //obj.Classement ClassementCumul { get; }
        decimal Value { get; set; }
        bool LastClassementRecent { get; }
        void setCoeff(decimal coeff, bool main);
        Piece Piece { get; }
        bool Empty { get; }
        decimal Coeff { set; get; }
        bool? getSimpleCause();
        //void finalyse();
        bool getCauseUp(bool? natureCause);
        bool UseCompact { get; set; }
#if NO_COEFF
        bool NoCoeff { get; set; }
#endif
        void Combine();
    }
}

using pdb.obj;
