namespace pdb.gen.albums
{

    public enum pieceExtendedMode
    {
        main,
        min,
        combine
    }

    public class PieceExtended : IPieceAlbum, IValue
    {

        private Piece piece;
        private PieceSupInfo main;
        private PieceSupInfo min;
        private PieceSupInfo combine;
        private static pieceExtendedMode _mode;
        private bool empty;
        ///// <summary>
        ///// Permet d'utiliser le classement Compact. 
        ///// </summary>
        //private bool useCompact; 
        //// private bool effectMain;


        public PieceExtended(Piece piece)
        {
            this.piece = piece;
            //   this.useCompact = piece.compact(); 
            //var cl = piece.RapportClassement(true);
            //if (cl == 0m)
            if (!piece.ClassmentPositive())
                empty = true;
            else
            {
                this.main = new PieceSupInfo(this);
                this.min = new PieceSupInfo(this);
            }
        }

        public void Combine()
        {
            if (empty)
                return;

            this.combine = new PieceSupInfo(this);
            var coeff = main.Coeff * min.Coeff;
            if (coeff > 1)
                combine.Coeff = coeff;

        }

        public decimal VirtualClassement { get { if (empty) return 0; return main.Value; } }
        public decimal VirtualClassement2 { get { if (empty) return 0; return min.Value; } }
        public decimal Cumul { get { if (empty) return 0; return combine.Value; } }
        public bool UseCompact { get { return piece.UseCompact; } set { piece.UseCompact = value; } }


        public bool getCauseUp(bool? natureCause)
        {
            if (empty)
                return false;
            if (true == natureCause)
                return min.Coeff > 1;
            if (false == natureCause)
                return main.Coeff > 1;
            return min.Coeff > 1 || main.Coeff > 1;
        }

        public bool? getSimpleCause()
        {
            if (empty)
                return null;

            var coeff1 = main.Coeff;
            var coeff2 = min.Coeff;
            if (coeff2 > coeff1)
                return true;
            else if (coeff1 > coeff2)
                return false;
            return null;
        }

        public decimal CoeffVirtuel { get { if (empty) return 1m; return main.Coeff; } }
        public decimal CoeffVirtuel2 { get { if (empty) return 1m; return min.Coeff; } }

        public Piece Piece { get { return piece; } }
        public bool Enabled { get { return piece.Enabled; } }
        public bool Empty { get { return empty; } }

        public bool LastClassementRecent { get { if (empty) return false; return piece.LastClassementRecent; } }
        public decimal RapportClassement { get { if (empty) return 0m; return piece.RapportClassement(true); } }
        public Classement Classement { get { return piece.getClassement(true); } }
        public string Hr { get { return RapportClassement.hr(); } }




        public void setCoeff(decimal coeff, bool main)
        {
            if (empty)
                return;

            if (main)
                this.main.Coeff = coeff;//   etCoeff(coeff, value0);
            else
                this.min.Coeff = coeff; // setCoeff(coeff, value0);
        }

        //public void setValue(decimal value, bool main)
        //{

        //    if (empty)
        //        return;
        //    if (main)
        //        this.main.Value = value;
        //    else
        //        this.min.Value = value;
        //}
#if GEN2
        public void finalyse()
        {

            if (empty)
                return;
            this.piece.setClassementVirtuel(this.main.Value);
            this.piece.setClassementVirtuel2(this.min.Value);

        }
#endif

        public override string ToString()
        {
            return string.Format("{0} enabled={1} coeff1={2} coeff2={3} main={4} min={5} p={6}", Hr, Enabled, CoeffVirtuel, CoeffVirtuel2, main.Hr, min.Hr, Piece);
        }


        internal PieceSupInfo Current
        {
            get
            {
                switch (_mode)
                {
                    case pieceExtendedMode.main: return main;
                    case pieceExtendedMode.min: return min;
                    case pieceExtendedMode.combine: return combine;
                    default: throw new NotImplementedException();
                }

            }
        }

        public decimal Value
        {
            get
            {
                if (empty)
                    return 0m;
                return Current.Value;
            }

            set
            {
                if (empty)
                    throw new ArgumentException("on ne peut affecter une valeur à un empty");
                if (value <= 0)
                    empty = true;
                else
                    Current.Value = value;
            }
        }

        public decimal Coeff
        {
            set
            {
                if (empty)
                    return;
                Current.Coeff = value;
            }
            get
            {
                return Current.Coeff; 
            }
        }

        public static pieceExtendedMode Mode
        {
            get { return _mode; }
            set { _mode = value; }
        }

        public bool NoCoeff
        {
            get
            {
                return false;
            }

            set
            {

            }
        }
        //  public bool EffectMain { get { return effectMain; } set { effectMain = value; } }
    }

    class PieceSupInfo
    {
        private PieceExtended piece;

        // private  decimal V0;
        // private decimal value;
        private decimal coeff;

        //private decimal V0 { get { return piece.RapportClassement(true); } }

        public PieceSupInfo(PieceExtended piece)
        {
            this.piece = piece;
            //   this.value = piece.RapportClassement(true);
            this.coeff = 1;
            // this.V0 = value; 
        }


        public decimal Coeff
        {
            get
            {
                return coeff;
                //if (value <= 0) return 1m;
                ////if (V0 / value > 1.1m)
                ////    Debugger.Launch();
                //return piece.RapportClassement(true) / value;
            }
            set
            {
                if (value > 1.1m)
                {
                }
                this.coeff = value;
                // this.value = piece.RapportClassement(true) / coeff;
            }

        }
        //public decimal getCoeff(decimal value0)
        //{
        //    if (value <= 0) return 1m;
        //}

        //public void setCoeff(decimal coeff, decimal value0)
        //{
        //    if (value0 > 0)
        //        this.value = value0 / coeff;

        //}

        public decimal Value
        {
            get
            {
                if (coeff <= 0)
                    return 0;
                return piece.RapportClassement / coeff;
            }
            set
            {
                if (value <= 0)
                {
                    coeff = 0;
                }
                else
                    coeff = piece.RapportClassement / value;
                if (coeff > 1.1m)
                {
                }
            }
        }
        public bool NoCoeff
        {
            get
            {
                return false;
            }

            set
            {

            }
        }

        public string Hr { get { return Value.hr(); } }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.obj;

namespace pdb.gen.albums
{
    class PieceExtendedSimple : IPieceAlbum
    {
        private Piece piece;
        private decimal coeff = 1m;
        private decimal value = -1;
#if NO_COEFF
        private bool noCoeff;
#endif
        private Classement classement = null;


        public PieceExtendedSimple(Piece piece)
        {
            this.piece = piece;
            piece.IPieceAlbum = this;
        }
        public Classement Classement
        {
            get
            {
                if (classement != null)
                    return classement;

                if (coeff == 1m)
                    classement = piece.getClassement(true);
                else if (coeff == 0)
                    classement = Classement.ZERO;
                else
                    classement = Classement.create(Value);

                return classement;
            }
        }
#if NO_COEFF
        public bool NoCoeff
        {
            get
            {
                return this.noCoeff;
            }
            set
            {
                if (value != this.noCoeff)
                {
                    if (value)
                    {
                        coeff = 1M;
                        this.classement = piece.getClassement(true);
                        this.value = this.classement.RapportClassement;
                    }
                    this.noCoeff = value;
                }
            }
        }
#endif

        public decimal Coeff
        {
            set
            {
                if (coeff != value)
                {
#if NO_COEFF
                    if (noCoeff)
                        return;
#endif
                    coeff = value;
                    this.value = -1;
                    this.classement = null;
                }

            }
            get
            {
                return coeff;
            }
        }

        public bool Empty
        {
            get
            {
                return Value == 0;
            }
        }

        public bool Enabled
        {
            get
            {
                return piece.Enabled;
            }
        }

        public bool LastClassementRecent
        {
            get
            {
                return piece.LastClassementRecent;
            }
        }

        public Piece Piece
        {
            get
            {
                return piece;
            }
        }

        public bool UseCompact
        {
            get
            {
                return piece.UseCompact;
            }

            set
            {
                piece.UseCompact = value;
            }
        }

        public decimal Value
        {
            get
            {
                if (value >= 0)
                    return value;
                else
                    value = piece.getClassement(true).RapportClassement / coeff;
                return value;
            }

            set
            {
                if (value != this.value)
                {
                    this.value = value;
                    classement = null;
                }
            }
        }

        public void Combine()
        {

        }

        public bool getCauseUp(bool? natureCause)
        {
            return coeff > 1;
        }

        public bool? getSimpleCause()
        {
            return null;
        }

        public void setCoeff(decimal coeff, bool main)
        {
            Coeff = coeff;
        }

        public override string ToString()
        {
            return string.Format("{0:0.#} {1} {2}", coeff, Classement, piece);
        }
    }
}

using System;
using pdb.gen.medianne;
using pdb.util;
using System.Xml;
using pdb.gen.auto;

namespace pdb.gen.Tuning
{
    [Serializable]
    public class ConfMedianne : TuningBase
    {
        public medianneMode type;
        public decimal medianne = 0.5m;
        public decimal quasi = 1m;

        public bool enabled;
        public decimal puissance = 1m;



        public bool removeFirst;
        public bool deleteFirst;
        public removeLast removeLast;

        public decimal expEnabled = 0m;

        public decimal zero = 0m;
        /// <summary>
        /// 
        /// </summary>
        public bool removeEnabled;
        /// <summary>
        /// cumule l'influence des deux parties main et min
        /// </summary>
        public bool combine;
        /// <summary>
        /// calcul simple des selectedMotif
        /// </summary>
        public bool simple;
        /// <summary>
        /// auto : si motifExclu on ne peut pas avoir un composant qui descent en même temps qu'un composant qui monte
        /// </summary>
        public bool motifExclu;
        /// <summary>
        /// Lors de la détermination du next; s'il est à zero, on force vers le keep
        /// </summary>
        public bool forceKeep;

        /// <summary>
        /// Lors de la détermination du next; s'il est à zero, on force vers le keep2
        /// </summary>
        public bool forceKeep2;

        /// <summary>
        /// Keep sert seulement à pas êter à zero. le classementValueLight ne doit pas baisser à cause d'un Keep
        /// </summary>
        public bool dontDownByKeep;

        /// <summary>
        /// Plutôt que d'aller vers le prochain zero, on tente le premier jamais noté
        /// </summary>
        public bool deepZero;

        /// <summary>
        /// Tant qu'il reste des non notés, on va vers eux
        /// </summary>
        public bool discover;

        /// <summary>
        /// Tant qu'il reste des non notés, on va vers eux, si le coeff est appliqué
        /// </summary>
        public bool discoverCoeff;


        public modeMin cmpMin;

        private ConfMedianneVirt _main;
        private ConfMedianneVirt _min;

        public ConfMedianneVirt Main { get { return _main; } }
        public ConfMedianneVirt Min { get { return _min; } }

        public ConfMedianne()
        {
            _main = new ConfMedianneVirt();
            _min = new ConfMedianneVirt();

        }

        public void makeIdem()
        {
            _min = _main;
        }

        public void duplicateMainIf()
        {
            if (_min == _main)
                _min = new ConfMedianneVirt(_main);
        }

        public ConfMedianne(ConfMedianne o, bool raz)
        {
            type = o.type;
            medianne = o.medianne;
            quasi = o.quasi;

            enabled = o.enabled;



            removeFirst = o.removeFirst;
            deleteFirst = o.deleteFirst;
            removeLast = o.removeLast;

            expEnabled = o.expEnabled;

            zero = o.zero;
            removeEnabled = o.removeEnabled;



            cmpMin = o.cmpMin;

            _main = new ConfMedianneVirt(o._main, raz);
            _min = new ConfMedianneVirt(o._min, raz);

            this.simple = o.simple;
            this.combine = o.combine;
            this.motifExclu = o.motifExclu;
            this.puissance = o.puissance;
            this.forceKeep = o.forceKeep;
            this.forceKeep2 = o.forceKeep2;
            this.dontDownByKeep = o.dontDownByKeep;
        }

        public ConfMedianne(XmlElement parent, XmlElement child)
            : base(parent, child)
        {
            var strType = "";
            builds("type", v => strType = v);
            type = (medianneMode)Enum.Parse(typeof(medianneMode), strType, true);
            builddec("medianne", v => medianne = v);
            builddec("quasi", v => quasi = v);
            buildb("enabled", v => enabled = v);
            buildb("removeFirst", v => removeFirst = v);
            buildb("deleteFirst", v => deleteFirst = v);
            builddec("expEnabled", v => expEnabled = v);
            builddec("zero", v => zero = v);
            buildb("removeEnabled", v => removeEnabled = v);
            string _modeMin = "";
            builds("cmpMin", v => _modeMin = v);
            if (!string.IsNullOrEmpty(_modeMin))
                cmpMin = (modeMin)Enum.Parse(typeof(modeMin), _modeMin, true);

            string _removeLast = null;
            builds("removeLast", v => _removeLast = v);
            removeLast = (removeLast)Enum.Parse(typeof(removeLast), _removeLast, true);

            buildb("combine", v => combine = v);
            buildb("simple", v => simple = v);
            buildb("motifExclu", v => motifExclu = v);

            builddec("puissance", v => puissance = v);
            buildb("forceKeep", v => forceKeep = v);
            buildb("deepZero", v => deepZero = v);
            buildb("forceKeep2", v => forceKeep2 = v);
            buildb("dontDownByKeep", v => dontDownByKeep = v);
            buildb("discover", v => discover = v);
            buildb("discoverCoeff", v => discoverCoeff = v);

            _main = null;
            _min = null;
            var subs = XMLTool.NodeLookUp(child, "virts");
            if (subs != null)
            {
                foreach (XmlNode _n in subs.ChildNodes)
                {
                    if (_n is XmlElement)
                    {
                        if (_main == null)
                        {
                            _main = new ConfMedianneVirt(child, _n as XmlElement);

                        }
                        else if (_min == null)
                        {
                            _min = new ConfMedianneVirt(child, _n as XmlElement);
                        }
                    }
                }
                if (_min == null && _main != null)
                    _min = new ConfMedianneVirt(_main);
            }
        }

        public override void save(XmlWriter w)
        {
            base.save(w);
            writeStart("cmp");
            write("type", type);
            write("medianne", medianne);
            write("quasi", quasi);
            write("puissance", puissance);
            write("enabled", enabled);
            write("removeFirst", removeFirst);
            write("deleteFirst", deleteFirst);
            write("expEnabled", expEnabled);
            write("zero", zero);
            write("removeEnabled", removeEnabled);
            write("cmpMin", cmpMin);
            write("removeLast", removeLast);
            write("combine", combine);
            write("simple", simple);
            write("motifExclu", motifExclu);
            write("forceKeep", forceKeep);
            write("deepZero", deepZero);
            write("forceKeep2", forceKeep2);
            write("dontDownByKeep", dontDownByKeep);
            write("discover", discover);
            write("discoverCoeff", discoverCoeff);

            writeStart("virts");
            _main.save(w);
            _min.save(w);
            writeEnd();

            writeEnd();



        }


