        public bool getStackInfoRecent(int seuil)
        {
            if (StackIndex < seuil && name != "\\")
            {
                if (nbRecent == -1)
                {
                    foreach (Piece p in list)
                    {
                        if (p.LastClassementRecent)
                        {
                            nbRecent =1;  
                            return true;
                        }
                    }
                    nbRecent = 0; 
                    return false;
                }
                return nbRecent > 0;
            }
            return false;
        }

