        #region Task


        private List<ITrackInfoItunes> loadTracks(bool firstStep, bool forceLoad)
        {
            List<ITrackInfoItunes> listTrack = new List<ITrackInfoItunes>();

            loader.init();
            loader.reset(firstStep, forceLoad);
            loader.loadTracks(listTrack);
            loader.loadLists();

            return listTrack;

        }
        #endregion

