        public bool Rare { get; set; }

        public void Add(TrackInfoItunes track)
        {
            list.Add(track); 
        }
        public void Remove(TrackInfoItunes track)
        {
            list.Remove(track); 
        }

        public List<TrackInfoItunes> List { get { return list; } }


    }
}

