using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.it.Albums
{
    class AlbumMgr : IAlbumMgr
    {
        private Dictionary<string, TrackAlbumBase> dict = new Dictionary<string, TrackAlbumBase>();
        public void setAlbum(TrackInfoItunes track, string album)
        {
            TrackAlbumBase _alb = null;
            if (!dict.ContainsKey(album))
            {
                _alb = new TrackAlbumBase(album);
                dict.Add(album, _alb);
            }
            else
                _alb = dict[album];
            track.TrackAlbum = _alb; 
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.it.Albums
{
    public interface IAlbum
    {
        string Album { get; }
    }

    public class TrackAlbumBase : IAlbum
    {
        private string name;
        public TrackAlbumBase(string name)
        {
            this.name = name; 
        }
        public string Album { get { return name; } }

        public override string ToString()
        {
            return name; 
        }

        public override bool Equals(object obj)
        {
            if (obj == null)
                return false; 
            return name == obj.ToString(); 
        }

        public override int GetHashCode()
        {
            return name.GetHashCode(); 
        }
    }

    public interface IAlbumMgr
    {
        void setAlbum(TrackInfoItunes track, string album); 
    }
}
