    public class HistoValue
    {
        char SEP = '|';
        public decimal coeff;
        public decimal delta;
        public decimal accel;
        // pour info
        public decimal dcoeffp;
        public decimal dv;
        public decimal vcoeff;

        //private HistoValue(string line)
        //{
        //    var tab = line.Split(SEP);
        //    int count = tab.GetLength(0);
        //    if (count >= 1 && !string.IsNullOrEmpty(tab[0]))
        //        coeff = Convert.ToDecimal(tab[0]);
        //    if (count >= 2 && !string.IsNullOrEmpty(tab[1]))
        //        delta = Convert.ToDecimal(tab[1]) / 1000m;
        //    if (count >= 3 && !string.IsNullOrEmpty(tab[2]))
        //        conv = Convert.ToDecimal(tab[2]) / 1000m;
        //}

        public HistoValue(string line, HistoValue last)
        {
            try
            {
                var tab = line.Split(SEP);
                int count = tab.GetLength(0);
                string str = "";
                if (count >= 1)
                {
                    str = tab[0];
                    if (!string.IsNullOrEmpty(str))
                        coeff = Convert.ToDecimal(str);
                    else if (last != null)
                        coeff = last.coeff;
                }
                if (count >= 2)
                {
                    str = tab[1];
                    if (!string.IsNullOrEmpty(str))
                        delta = Convert.ToDecimal(str) / 1000m;
                    else if (last != null)
                        delta = last.delta;
                }
                if (count >= 3)
                {
                    str = tab[2];
                    if (!string.IsNullOrEmpty(str))
                        accel = Convert.ToDecimal(str) / 1000m;
                    else if (last != null)
                        accel = last.accel;
                }

                if (count >= 4)
                {
                    str = tab[3];
                    if (!string.IsNullOrEmpty(str))
                        dcoeffp = Convert.ToDecimal(str) / 1000m;
                    else if (last != null)
                        dcoeffp = last.dcoeffp;
                }

                if (count >= 5)
                {
                    str = tab[4];
                    if (!string.IsNullOrEmpty(str))
                        dv = Convert.ToDecimal(str) / 1000m;
                    else if (last != null)
                        dv = last.dv;
                }

                if (count >= 6)
                {
                    str = tab[5];
                    if (!string.IsNullOrEmpty(str))
                        vcoeff = Convert.ToDecimal(str) / 1000m;
                    else if (last != null)
                        vcoeff = last.vcoeff;
                }
            }
            catch (Exception e)
            {
                misc.log("impossible de parser " + line, e);
                throw;
            }
        }

        public HistoValue(decimal coeff, decimal delta, decimal accel, decimal dcoeff, decimal dv, decimal vcoeff)
        {
            this.coeff = coeff;
            this.delta = delta;
            this.accel = accel;
            this.dcoeffp = dcoeff;
            this.dv = dv;
            this.vcoeff = vcoeff;
        }

        public override string ToString()
        {
            var sb = new StringBuilder();
            write(sb);
            return sb.ToString();
        }

        public void write(ITextWriter sb)
        {
            var v = Math.Round(coeff, 12);
            var sc = v.ToString();
            var v2 = Math.Round(1000m * delta, 10);
            var sc2 = v2.ToString();

            var v3 = Math.Round(1000m * accel, 10);
            var sc3 = v3.ToString();

            var v4 = Math.Round(1000m * dcoeffp, 10);
            var sc4 = v4.ToString();

            var v5 = Math.Round(1000m * dv, 10);
            var sc5 = v5.ToString();

            var v6 = Math.Round(1000m * vcoeff, 10);
            var sc6 = v6.ToString();

            sb.Append(sc);
            sb.Append(SEP);
            sb.Append(sc2);
            sb.Append(SEP);
            sb.Append(sc3);
            sb.Append(SEP);

            sb.Append(sc4);
            sb.Append(SEP);
            sb.Append(sc5);
            sb.Append(SEP);
            sb.AppendLine(sc6);
        }

        private static decimal getTruncate(decimal v)
        {
            return Math.Round(1000m * v, 10);
        }

        public void writeDiff(ITextWriter sb, HistoValue last)
        {


            var v = Math.Round(coeff, 12);
            var sc = v.ToString();
            sb.Append(sc);

            sb.Append(SEP);
            var v2 = getTruncate(delta);
            if (last == null || v2 != getTruncate(last.delta))
            {
                var sc2 = v2.ToString();
                sb.Append(sc2);
            }

            sb.Append(SEP);
            var v3 = getTruncate(accel);
            if (last == null || v3 != getTruncate(last.accel))
            {
                var sc3 = v3.ToString();
                sb.Append(sc3);
            }
            sb.Append(SEP);

            var v4 = getTruncate(dcoeffp);
            if (last == null || v4 != getTruncate(last.dcoeffp))
            {
                var sc4 = v4.ToString();
                sb.Append(sc4);
            }
            sb.Append(SEP);
            var v5 = getTruncate(dv);
            if (last == null || v5 != getTruncate(last.dv))
            {
                var sc5 = v5.ToString();
                sb.Append(sc5);
            }
            sb.Append(SEP);
            var v6 = getTruncate(vcoeff);
            if (last == null || v6 != getTruncate(last.vcoeff))
            {
                var sc6 = v6.ToString();
                sb.Append(sc6);
            }
            sb.AppendLine();

        }
    }
