            set
            {
                if (value != position)
                {
                    position = value;
                    Player.Position = value;
                    this.OnPropertyChanged("Position");
                }
            }
        }

        private String currentName;
        public String CurrentName
        {
            get { return currentName; }
            set
            {
                if (value != currentName)
                {
                    currentName = value;
                    this.OnPropertyChanged("CurrentName");
                }
            }
        }

        private double volume = 0.5;
        public double Volume
        {
            get { return volume; }
            set
            {               
                if (value != volume)
                {
                    volume = value;
                    Player.Volume = value;
                    OnPropertyChanged("Volume");
                }
            }
        }


        private String currentAuthorAlbum;
        public String CurrentAuthorAlbum
        {
            get { return currentAuthorAlbum; }
            set
            {
                if (value != currentAuthorAlbum)
                {
                    currentAuthorAlbum = value;
                    this.OnPropertyChanged("CurrentAuthorAlbum");
                }
            }
        }

        private TimeSpan sinceBeginReadTrack;
        public TimeSpan ElapsedTime
        {
            get { return sinceBeginReadTrack; }
            set
            {
                if (value != sinceBeginReadTrack)
                {
                    sinceBeginReadTrack = value;
                    this.OnPropertyChanged("ElapsedTime");
                }
            }
        }

        private TimeSpan remainingTime;
        public TimeSpan RemainingTime
        {
            get { return remainingTime; }
            set
            {
                if (value != remainingTime)
                {
                    remainingTime = value;
                    this.OnPropertyChanged("RemainingTime");
                }
            }
        }

        private TimeSpan length;
        public TimeSpan Length
        {
            get { return length; }
            set
            {
                if (value != length && value > TimeSpan.FromMilliseconds(100))
                {
                    length = value;
                    this.OnPropertyChanged("Length");
                }
            }
        }
        #endregion
    }
}

using System.IO;
using System;
namespace pdb.obj
{
    public enum FileType : short
    {
        none =-100,
        jpg = -10,
        png = -11,
        tif = -12,
        tiff = -13,
        gif= -14,

        unknown = -1,
        mp3 = 1,
        mp4 = 2,
        wma = 3,
        m4v = 4,
        m4a = 6,
        ogg = 8,
        aac = 10,       
        wav = 11,
        flac=30
    }

    public enum listMode
    {
        substract,
        rapport,
        logPure,
        log,
        logAbs,
        linear

    }

    public static class FileUtil
    {
        public static string ext(this FileInfo file)
        {
            return file.Extension.ToLower().Replace(".",""); 
        }

        public static FileType type(this FileInfo file)
        {
            try
            {
                return (FileType)Enum.Parse(typeof(FileType), file.ext()); 
            }
            catch 
            {
                return FileType.unknown; 
            }
        }

        public static FileType type(this string file)
        {
            string ext = Path.GetExtension(file).Replace(".", "").ToLower();

            try
            {
                return (FileType)Enum.Parse(typeof(FileType),ext);
            }
            catch
            {
                return FileType.unknown;
            }
        }

        public static bool isMusic(this string file)
        {
            return file.type().typeMusic(); 
        }

        public static bool typeImg(this FileType type)
        {
            return (int)type <= -10; 
        }

        public static bool typeMusic(this FileType type)
        {
            return (int)type > 0;
        }

        public static bool IsImage(this FileInfo file)
        {
            return file.type().typeImg(); 
        }

        public static bool IsMusic(this FileInfo file)
        {
            return file.type().typeMusic();
        }

    }


}
