        private int scrollIndex; 
        void scrol()
        {
            if (!CheckAccess())
            {
                Dispatcher.Invoke(new Action(this.scrol));
            }
            else
            {
                if (dg.Items.Count == 0)
                    return;
                int index = scrollIndex; 
                if (index < 0)
                    index = 0;
                if (index >= dg.Items.Count)
                    index = dg.Items.Count - 1;

                dg.ScrollIntoView(dg.Items[index]);
            }
        }
