            //{
            //    zip.CompressDirectory(dir, baseStream); 
            //    //zip.CompressStreamDictionary(dict, baseStream); 
            //}

            foreach (var file in dirr.GetFiles())
            {
                file.Delete();
            }

            dirr.Delete();

            //foreach (Stream s in dict.Values)
            //{
            //    s.Dispose(); 
            //}

        }

