        public decimal dcoeffm;
        public decimal dconvergence;

        private bool? lastAutoDirection; 

        public ConfMedianneVirt()
        { }
        public ConfMedianneVirt(ConfMedianneVirt o)
        {
            safe = o.safe;
            coeffVirt = o.coeffVirt;
