        HashList<Piece> trackSortedByExportPriority;
        private class PieceExportComparer : IComparer<Piece>
        {
            private HashList<Piece> source;
            private Piece next;
            private int inext = -1;
            public PieceExportComparer(HashList<Piece> source, Piece next)
            {
                this.source = source;
                this.next = next;
                if (next != null)
                    inext = source.IndexOf(next);
            }
            public int Compare(Piece x, Piece y)
            {
                //    t = 1.CompareTo(2) -> -1              
                //  t = true.CompareTo(false) ->1
                if (x == y)
                    return 0; 
                if (x == next)
                    return -1;
                if (y == next)
                    return 1;
                int ix = source.IndexOf(x);
                int iy = source.IndexOf(y);
                if (ix == iy)
                    return 0; 
                if (next != null)
                {
                    int dx = ix - inext;
                    int dy = iy - inext;

                    if (dx * dy < 0)
                    {
                        if (dx > 0 && !x.Enabled)
                            return 1;
                        if (dy > 0 && !y.Enabled)
                            return -1;
                    }

                    int cmp = Math.Abs(dx).CompareTo(Math.Abs(dy));
                    if (cmp != 0)
                        return cmp;
                    //ici même distance
                    cmp = x.Enabled.CompareTo(y.Enabled);
                    if (cmp != 0) return -cmp;

                    cmp = ix.CompareTo(iy);
                    return cmp;
                }

                return source.IndexOf(x).CompareTo(source.IndexOf(y));
            }
        }
