                    //else if (classement < 2)
                    //{ moy = 12; nbPieceByLevel = 2; }
                    //else { moy = 6; nbPieceByLevel = 1; }

                    // 1234.2  au million 1234.2 ==> 1 234 200
                    //cas densite 1 100
                    //cas densite 2 100
                    // cas densite 3 100

                    // valeurs possibles de l'increment 1,2,3,10,20,30,100,200
                    //// maintenant 1234.2 ==> 1 234 200
