                    misc.log("compact phase {0} courant {1} classement={2} densite {3} ", phase, currentListLevel, piece.ClassementValueDecimal(false), densite);

                    if (index >= total)
                        break;
                    decimal nbth = densite * 1296.0m;
                    int nbARepartir = (int)System.Math.Ceiling(nbth);

