    /// <summary>
    /// M√©tadonn√©es d'un fichier
    /// </summary>
    /// <remarks>On veut pouvoir merger les modifications venant de deux sources
    /// exemeple : un modifie le classement, l'autre l'ann√©e
    /// Pour cela, il est envisag√© d'enregistrer pour chaque modification effectu√© par le client
    /// tout simplement la date de cette modification
    /// le merge se baserait ainsi sur la comparaison entre les dates de modification
    /// source 1 date modif d1' pour classement > d1
    /// source 2 date modif d2' pour ann√©e > d2
    /// 
    /// On pourrait aussi se baser sur un flag => mettre √† jour
    /// ainsi on gagnerait de la place, mais on perdrait la date de derniere mise √† jour
    /// et se poserait aussi la question de quand retirer le flaf "mettre √† jour"
    /// 
    /// On peut aussi √©tablir un historique
    ///
    ///</remarks>
    /// 


    public class CPiece : IData, ITrackMetaData, ITrackNativeDates, ITrackPlayListRecorder, ITrackPlayListReport, IDataObserver, ITrackInfoItunes, IItem
    {
        private static Logger logTrack = Logger.getLogger("Track");
