using System.IO;
using System.Threading.Tasks;
using pdb.util.list;
using System.Collections.Generic;
using System;

namespace pdb.util
{
    public class FileRegister
    {
        private static FileRegister _instance = new FileRegister();
        private BgDictString0<FileValue> dict = new BgDictString<FileValue>();
        private BgDictString0<DirValue> dirs = new BgDictString<DirValue>();
        private static volatile object _lock = new object();

        /// <summary>
        /// Thread de dépilage en parallèles des fichiers à checker
        /// </summary>
        private ThreadUtil th;
        /// <summary>
        /// Pile filo des fichiers à checker
        /// </summary>
        pdb.util.list.Stack<string> stack = new pdb.util.list.Stack<string>();
        DateTime stackEmpty; 

        public static void init(BgDictString0<FileValue> dict)
        {
            _instance.dict = dict;
        }


        private DirValue checkDir(FileInfo fmusic)
        {
            var dir = fmusic.Directory;
            if (dirs.ContainsKey(dir.FullName))
                return dirs[dir.FullName];
            var dirV = new DirValue(fmusic);
            dirs.Add(dirV.FullName, dirV);
            return dirV;
        }

        private DirValue checkDir(string fmusic)
        {
            var dir = Path.GetDirectoryName(fmusic);
            if (dirs.ContainsKey(dir))
                return dirs[dir];
            var dirV = new DirValue(fmusic);
            dirs.Add(dirV.FullName, dirV);
            return dirV;
        }

        public static DirValue CheckDir(FileInfo fmusic)
        {
            return _instance.checkDir(fmusic);
        }

        public static DirValue CheckDir(string fmusic)
        {
            return _instance.checkDir(fmusic);
        }


        private FileValue check(FileInfo infos)
        {
            if (!dict.ContainsKey(infos.FullName))
            {
                bool _exists = false;
                string fullName = "";

                long length = 0;
                try
                {

                    _exists = infos.Exists;
                    fullName = infos.FullName;



                    if (_exists)
                        length = infos.Length;

                }
                catch { }
                var value = new FileValue(fullName, length, _exists);
                dict.Add(fullName, value);
                return value;
            }

            return dict[infos.FullName];
        }

        private FileValue check(string location)
        {
            if (!dict.ContainsKey(location))
            {
                bool _exists = false;
                string fullName = "";

                long length = 0;
                try
                {

                    FileInfo infos = new FileInfo(location);
                    _exists = infos.Exists;
                    fullName = infos.FullName;

                    //DirectoryInfo dir = infos.Directory;
                    //dirAndFileName = dir.Name + Path.DirectorySeparatorChar + infos.Name; 

                    if (_exists)
                        length = infos.Length;

                }
                catch { }
                var value = new FileValue(fullName, length, _exists);
                if (dict.ContainsKey(location))
                {
                    misc.log("trop bizarre on a rempli le dict avec " + location);
                }
                else
                    dict.Add(location, value);
                return value;
            }
            return dict[location];
        }

        private FileValue delete(string location)
        {
            var value = check(location);
            value.delete();
            return value;

        }

        private void push(string location)
        {
            if (dict.ContainsKey(location))
                return; 
            stack.Push(location);

            if (th == null)
            {
                lock (_lock)
                {
                    if (th == null)
                    {
                        th = new ThreadUtil(loopCheck, "FileRegister.loopCheck");
                        th.Start();
                    }
                }
            }
        }

        private void loopCheck()
        {
            while (true)
            {
                var location = stack.Peek();
                if (location == null)
                {
                    var now = DateTime.Now;
                    if (stackEmpty > DateTime.MinValue)
                    {
                        if ((now - stackEmpty).TotalMinutes > 1)
                        {
                            lock (_lock)
                            {
                                location = stack.Peek();
                                if (location == null)
                                {
                                    th = null;
                                    return; 
                                }
                            }
                        }
                    }
                    ThreadUtil.Sleep(30);
                    continue;
                }
                check(location);

            }
        }

        public static FileValue Check(FileInfo f)
        {
            return _instance.check(f);
        }

        public static FileValue Check(string location)
        {
            return _instance.check(location);
        }

        public static bool Exist(string location)
        {
            return _instance.check(location).Exists;
        }
        public static string FullName(string location)
        {
            return _instance.check(location).FullName;
        }
        public static long Length(string location)
        {
            return _instance.check(location).Length;
        }
        public static FileValue Delete(string location)
        {
            return _instance.delete(location);
        }
        public static void Push(string location)
        {
            _instance.push(location);
        }
    }


    public class FileValue
    {
        public readonly string FullName;
        private bool _exists;
        public long Length;
        //public readonly string DirAndFileName;
        public FileValue(string fullName, long length, bool exists)
        {
            this.FullName = fullName;
            // this.DirAndFileName = dirAndFileName; 
            this.Length = length;
            this._exists = exists;
        }

        public bool Exists { get { return _exists; } }
        public override string ToString()
        {
            return string.Format("{0} {1} {2}", Exists, FullName, Length);
        }

        public void delete() { _exists = false; }
        public void copy(long length)
        {
            _exists = true;
            Length = length;
        }

        public override bool Equals(object obj)
        {
            var o = obj as FileValue;
            if (o == null) return false;
            return FullName == o.FullName; //&& Exists == o.Exists;
        }
        public override int GetHashCode()
        {
            return FullName.GetHashCode(); // +Length.GetHashCode();
        }
    }

    public class DirValue
    {
        private bool _exists;
        public readonly string FullName;
        public readonly List<FileValue> imgs = new List<FileValue>();
        public DirValue(FileInfo f)
        {

            var dir = f.Directory;
            FullName = dir.FullName;
            init(dir);
            _exists = true;
        }
        private void init(DirectoryInfo dir)
        {
            imgs.Clear();
            foreach (FileInfo ff in dir.GetFiles())
            {
                if (ff.IsImage())
                {
                    var fff = FileRegister.Check(ff);
                    imgs.Add(fff);
                }
            }

        }

        public DirValue(string path)
        {
            FullName = Path.GetDirectoryName(path);
            _exists = Directory.Exists(FullName);
            if (_exists)
                init(new DirectoryInfo(FullName));
        }

        public bool Exist { get { return _exists; } set { _exists = value; } }
    }


}

using System;
using System.Collections.Generic;
using System.Threading;

namespace pdb.util
{
    public class ThreadUtil
    {
        private Thread th;
        private static volatile object _lock = new object();
        private static Logger log = Logger.getLogger("ThreadUtil",false); 

        private ThreadStart exec;
        private static List<ThreadUtil> list = new List<ThreadUtil>();
        private static bool _log = true;
        public static bool Log { set { _log = value; } }
        private bool _end;
        public bool End { get { return _end; } }
        private static void displayScan(List<ThreadUtil> list)
        {
            if (!_log) return;

            log.log("nb Threads:" + list.Count);
            foreach (ThreadUtil thread in list)
                log.logNoDate(thread.ToString());
        }

        public ThreadUtil(ThreadStart exec, string name)
        {
            this.exec = exec;
            th = new Thread(execute);
            try
            {
                th.Name = name;
            }
            catch
            {
            }
        }

        public void Start()
        {
            th.Start();
        }
        public static void Sleep(int ms)
        {
            Thread.Sleep(ms); 
        }

        public string Name { get { return th.Name; } }

        public ThreadState State { get { return th.ThreadState; } }


        private void execute()
        {
            List<ThreadUtil> aux = null;
            lock (_lock)
            {
                list.Add(this);
                if (_log)
                    aux = new List<ThreadUtil>(list);
            }

            log.log("===> démarrage de " + Name);
            displayScan(aux);
            try
            {
                exec();
            }

            catch(System.Runtime.CompilerServices.RuntimeWrappedException)
            {
                log.log("erreur non gérée");
            }
            catch (Exception e)
            {
                log.log(e.ToString());
            }
            finally
            {
                _end = true; 
                log.log("===> fin de " + Name);
                lock (_lock)
                {
                    list.Remove(this);
                    if (_log)
                        aux = new List<ThreadUtil>(list);
                }
                displayScan(aux);
            }
        }



        public void Abort()
        {
            th.Abort(); 
        }

        public override string ToString()
        {
            if (string.IsNullOrEmpty(Name))
                return th.ManagedThreadId.ToString();
            return Name;

        }

        public override bool Equals(object obj)
        {
            var o = obj as ThreadUtil;
            if (o == null) return false;
            return th.ManagedThreadId.Equals(o.th.ManagedThreadId);
        }


        public override int GetHashCode()
        {
            return th.ManagedThreadId.GetHashCode();
        }


    }
}

using System;
