using System.Text;

namespace pdb.util.list
{
    /// <summary>
    /// Pile Filo synchronisée
    /// </summary>
    /// <typeparam name="T"></typeparam>
    class Stack<T> where T : class
    {
        private LinkedList first;
        private volatile object _lock = new object();
        /// <summary>
        /// Ajout d'un élément en début de liste
        /// </summary>
        /// <param name="item"></param>
        public void Push(T item)
        {
            lock (_lock)
            {
                var ll = new LinkedList(item);

                if (first == null)
                {
                  //  misc.log("Stack.Push {0} first=null", item); 
                    first = ll;
                }
                else
                {
                  //  misc.log("Stack.Push {0} first={1}", item, first); 
                    ll.next = first;
                    first = ll;
                }
            }
        }
        /// <summary>
        /// Récupération - et retrait du premier élément de la pile
        /// </summary>
        /// <returns></returns>
        public T Peek()
        {
            var peek = first;
            if (peek == null)
                return null;
            lock (_lock)
            {
                peek = first;
                if (peek == null)
                    return null;
                first = peek.next; 
            }
            return peek.item; 
        }
        /// <summary>
        /// Liste liée
        /// </summary>
        private class LinkedList
        {
            public readonly T item;
            public LinkedList next;
            public LinkedList(T data)
            {
                this.item = data;
            }

            public override string ToString()
            {
                return string.Format("{0} {1}", item, next == null ? "" : next.item.ToString()); 
            }
        }
    }
}

