        public FileType FileType
        {
            get
            {
                if (fileType == FileType.none)
                {
                    fileType = FileType.unknown;
                    String ext = System.IO.Path.GetExtension(m_file).Replace(".", "").ToLower();


                    try
                    {
                        fileType = (FileType)Enum.Parse(typeof(FileType), ext, true);
                    }
                    catch
                    {
                    }
                }
                return fileType;
            }
