                load();
                return;
            }

            using (tw = new System.IO.StreamWriter(FILE))
            {

                decimal densite = 1.0m;

                moy = total + 100;
                for (int i = 0; i < MAX_LEVEL; i++)
                {
                    moy /= PART;
                }
                currentListLevel = new ClassStruct(); // list[0].Piece.ClasssementVakdlue;
