                    var v2 = piece.RapportClassement;

                    if (v2 > v)
                        v = v2;

                    if (v < 1)
                    {
                        if (phase == 0)
                            densite = 6;
                        else if (phase < 3)
                            densite = 6 - phase;
                        else
