                    if (conf.dconvergence > 0)
                    {
                        percentConv = conf.dconvergence0 / conf.dconvergence;
                        sb.Append((percentConv).ToString("### ### ##0", CultureInfo.InvariantCulture)); sb.Append("/");
                        percentConv = conf.danticonvergence / conf.danticonvergenceMin;
