using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Runtime.Serialization.Formatters.Binary;
using System.IO;
using System.Windows;
using pdb.gen.Tuning;
using System.Xml;
using pdb.util;

namespace pdb.player.Vue
{
    [Serializable]
    class Memento
    {
        [NonSerialized]
        const string FILE_CONF = "confAlbum.xml";
        [NonSerialized]
        const string PATH = TuningBase.DIR_CONF + "memento.dat";
        [NonSerialized]
        const string PATH_CONF = TuningBase.DIR_CONF + FILE_CONF;
        [NonSerialized]
        const string SOV_CONF = TuningBase.DIR_CONF + "sov\\";
        [NonSerialized]
        private static volatile Memento _instance;
        [NonSerialized]
        private FileRotator fileRotator;
        public WindowPos winPos = null;
        public CmpPos cmpPos = null;
        public string selectedPlaylist = "";
        public Dictionary<string, object> dict = new Dictionary<string, object>();
        [NonSerialized]
        public ConfAlbumSort confAlbum;


        private Memento()
        {
            fileRotator = new FileRotator(); 
        }
        public static Memento Instance
        {
            get
            {
                if (_instance == null)
                {

                    var formater = new BinaryFormatter();
                    try
                    {
                        using (Stream st = new FileStream(PATH, FileMode.Open))
                        {
                            _instance = (Memento)(formater.Deserialize(st));
                        }
                        var doc = new XmlDocument();
                        doc.Load(PATH_CONF);
                        var conf = new ConfAlbumSort(doc.DocumentElement);

                        _instance.confAlbum = conf;
                    }
                    catch (Exception ex)
                    {
                        misc.log(ex.ToString());
                        throw;
                    }


                }

                if (_instance == null)
                    _instance = new Memento();

                _instance.fileRotator = new FileRotator(); 
                return _instance;

            }
        }
        public void save()
        {
            var formater = new BinaryFormatter();
            using (Stream st = new FileStream(PATH, FileMode.Create))
            {
                formater.Serialize(st, this);
            }
            if (this.confAlbum != null)
            {
                fileRotator.save(PATH_CONF, SOV_CONF, 100);
                var settings = new XmlWriterSettings();
                settings.Indent = true;
                settings.Encoding = Encoding.UTF8;
                using (XmlWriter writer = XmlWriter.Create(PATH_CONF, settings))
                {
                    this.confAlbum.save(writer);
                }
            }
        }
    }
    [Serializable]
    class WindowPos
    {
        public double Top;
        public double Left;
        public double Width;
        public double Height;
    }
    [Serializable]
    class CmpPos
    {
        public Length cdBib;
        public Length cdMain;
        public Length cdAlbum;
        public Length cdClassement;
    }

    [Serializable]
    public class Length
    {
        public double value;
        public GridUnitType unit;
        public Length(GridLength ihm)
        {
            value = ihm.Value;
            unit = ihm.GridUnitType;
        }
        public GridLength toGrid()
        {
            return new GridLength(value, unit);
        }
    }
}

using System;
using System.Windows.Controls;
using pdb.db.piece.type;
using pdb.gen;
using pdb.gen.Tuning;
using pdb.gen.medianne;

namespace pdb.player.Vue.Conf
{
    /// <summary>
    /// Interaction logic for ConfMedianneW.xaml
    /// </summary>
    public partial class ConfMedianneW : UserControl
    {

        private ConfMedianne conf;
        private ConfVirt main;
        private ConfVirt min;
        private IConfSort parent;
        public ConfMedianneW()
        {
            InitializeComponent();

        }

        public ConfMedianneW(ConfMedianne conf, IConfSort parent)
            : this()
        {
            this.conf = conf;
            this.parent = parent;

            this.main = new ConfVirt(conf.Main, parent);
            this.min = new ConfVirt(conf.Min, parent);

            gmain.Children.Add(main);
            gmin.Children.Add(min);

            InitializeComponent();

        }

        public void checkIdem()
        {
            if (parent.DuplicateItem)
            {
                conf.makeIdem();
                min.IsEnabled = false;
            }
            else
            {
                min.IsEnabled = true;
                conf.duplicateMainIf();
            }


            min.Conf = conf.Min;
        }

        public void display()
        {



            cb_enabled.IsChecked = conf.enabled;
            cbxSort.SelectedIndex = (int)conf.type;
            tb_medianne.Text = conf.medianne.ToString();
            cb_quasi.IsChecked = conf.quasi;

            cb_removeFirst.IsChecked = conf.removeFirst;
            cb_deleteFirst.IsChecked = conf.deleteFirst;
            cbx1_removeLast.SelectedIndex = (int)conf.removeLast;

            tb_exp.Text = conf.expEnabled.ToString();
            tb_zero.Text = Piece.getClassementEquiv(conf.zero);
            cb_removeEnabled.IsChecked = conf.removeEnabled;

            cbx_cmpMinn.SelectedIndex = (int)conf.cmpMin;

            main.display();
            checkIdem();
            min.display();

        }

        public void record()
        {

            conf.enabled = cb_enabled.IsChecked.Value;
            conf.type = (medianneMode)cbxSort.SelectedIndex;
            conf.medianne = Convert.ToDecimal(tb_medianne.Text);
            conf.quasi = cb_quasi.IsChecked.Value;

            conf.removeFirst = cb_removeFirst.IsChecked.Value;
            conf.deleteFirst = cb_deleteFirst.IsChecked.Value;
            conf.removeLast = (removeLast)cbx1_removeLast.SelectedIndex;

            conf.expEnabled = Convert.ToDecimal(tb_exp.Text);
            var cl = Classement.create(tb_zero.Text);
            conf.zero = cl.RapportClassement;
            conf.removeEnabled = cb_removeEnabled.IsChecked.Value;

            conf.cmpMin = (modeMin)cbx_cmpMinn.SelectedIndex;

            main.record();
            if (!parent.DuplicateItem)
                min.record();



        }

        public void onchange()
        {
            main.onchange();
            min.onchange();
        }
    }
}

