        /// </summary>
        private bool selectedSure;
        public bool DontTouchSelectedCoeff { get { return selectedSure; } }
        public void InvalidateMain()
        {
            if (selectedCoeff > selectedCoeff.noCoeff)
                selectedCoeff = selectedCoeff.noCoeff;
        }


