        private PlayListViewModel _listes; 
        public PlayListViewModel Listes
        {
            get
            {
                if (_listes == null)
                {
                    foreach (PlayListViewModel sub in root)
                    {
                        if (sub.Name == "listes")
                        {
                            _listes = sub;
                            break; 
                        }
                    }
                }
                return _listes; 
            }
        }

