using System;
using pdb.db.obj;
using pdb.obj;
namespace pdb.db
{
    public static class TypeComparer
    {
        public static bool isEmpty(this String str)
        {
            return String.IsNullOrEmpty(str);
        }

        public static bool isEmpty(this int i)
        {
            return (i <= 0);
        }

        public static bool isEmpty(this Classement i)
        {
            return (!i.isClassed());
        }

        public static bool isSignificant(this String str) { return !str.isEmpty(); }
        public static bool isSignificant(this int i) { return !i.isEmpty(); }
        public static bool isSignificant(this Classement i) { return (i.isClassed()); }

        public static mergePriority compareByConsistency(this String str1, String str2)
        {
            if (!str1.isSignificant() && isSignificant(str2))
                return mergePriority.right;
            if (str1.isSignificant() && !isSignificant(str2))
                return mergePriority.left;
            return mergePriority.none;
        }
        public static mergePriority compareByConsistency(this int str1, int str2)
        {
            if (!str1.isSignificant() && isSignificant(str2))
                return mergePriority.right;
            if (str1.isSignificant() && !isSignificant(str2))
                return mergePriority.left;
            return mergePriority.none;
        }

    }
}

