                    { }
                    return this;
                }
                return parent;
            }
        }


        public void setId(int value)
        {
            if (m_id == value)
                return;
            if (value > 0)
            {
                key = null;
                if (m_id > 0)
                    misc.log("id from {0} to {1} {2}", m_id, value, this);
                m_id = value;
            }
        }

