        public string ClassementCompact
        {
            get
            {
                if (parent != null)
                    return parent.ClassementCompact; 
#if COMPACT
                if (string.IsNullOrEmpty(_classementCompact))
                    return ClassementCache.strClassement;
                return _classementCompact;
#else 
                return ClassementCache.strClassement; 
#endif
            }
