        public VirtualPiece(ITrackNativeDates track, Piece _real)
            : base(track)
        {
            this._real = _real;
            this.Parent = _real;
            idSeq--;
            vid = idSeq;
        }

        public override int PieceId
        {
            get
            {
                return vid;
            }
        }

        public override int PieceParentId
        {
            get
            {
                return _real.PieceId;
            }
            set
            {

            }
        }

