        {
            borderObs.Add(auto);
        }
        #endregion
        #endregion

        public void signal(TrackBorderItem tb)
        {
            if (false == HistoAll)
                return; 
            bool _checked = false;
            foreach (IBorderTransmit border in borderObs)
            {
                border.signal(tb);
                if (border.Checked)
                    _checked = true; 
            }

            if (_checked)
                return; 
            if (parent != null)
                parent.signal(tb);
        }
    }
}

