        public List<TrackInfoItunes> getSelectedTracks
        {
            get
            {
                var list = tracks.FindAll(t => t.Selected);
                list.Sort(new PodCastComparer());
                return list;

            }
        }

        //public List<TrackInfoItunes> getPreSelectedTracks
