using System;
namespace pdb.podcast
{
    /// <summary>
    /// Objet contenant des propriétés permettant la sélection
    /// </summary>
    public interface ISelectable
    {
        string Album { get; }
        int Rating { get; }
        bool Actu { get; }

        int AlbumRating { get; }

        pdb.util.timeSpan Duree { get; }
        pdb.podcast.Tuning.LimitItem Exclu { get; set; }
        bool Histo { get; }
        bool Info { get; }
        DateTime Pub { get; }
        DateTime Pub2 { get; set; }
        bool Rare { get; }
        bool Serial { get; }
        long Size { get; }
        pdb.podcast.Albums.albumSpec Specif { get; }
    }
    /// <summary>
    /// Selection ou pas d'un objet contenant des propriétés permettant la sélection
    /// </summary>
    interface ITrackSelector
    {
        bool select(ISelectable t);
    }
}

