        public string LogicalName
        {
            get
            {
                //   string infoPubName = pub.ToString("dd.MM.yyyy");
                // string infoPubName2 = pub.ToString("dd/MM/yyyy");
                //string infoPubName3 = pub.ToString("dd/MM/yy");

                var t = name; var sw = getDayOfWeek();
                if (t.EndsWith(sw))
                    t = t.Remove(t.Length - sw.Length).Trim();
                var strPub = pub.ToString("dd.MM.yyyy");
                if (t.EndsWith(strPub))
                    t = t.Remove(t.Length - strPub.Length).Trim();
                strPub = pub.ToString("dd/MM/yyyy");
                if (t.EndsWith(strPub))
                    t = t.Remove(t.Length - strPub.Length).Trim();
                strPub = pub.ToString("dd/MM/yy");
                if (t.EndsWith(strPub))
                    t = t.Remove(t.Length - strPub.Length).Trim();
                strPub = pubInfoShort();
                if (t.EndsWith(strPub))
                    t = t.Remove(t.Length - strPub.Length).Trim();

                for (int i = 0; i < 3; i++)
                {
                    if (t.Contains("  "))
                        t = t.Replace("  "," "); 
                }



                return t;

                //string end = string.Format("{0} {1}", pubInfoShort(), getDayOfWeek());
                //return name.TrimEnd(end.ToCharArray()); 

            }
        }

        public override TimeSpan Duration
        {
            get
            {
                var dur = base.Duration;
                if (dur.TotalSeconds == 0)
                {
                    base.Duration = TimeSpan.FromSeconds(duree.Value);
                }
                return base.Duration;
            }

            set
            {
                base.Duration = value;
                if (duree.Value == 0)
                {
                    duree = new timeSpan(base.Duration);
                }
            }
        }

        private long _size = -1;
        public long Size
        {
            get
            {
                if (_size < 0)
                {
                    var f = FileRegister.Check(Location);
                    if (f.Exists)
                        _size = f.Length;
                    else
                        _size = 0;
                }
                return _size;
            }
        }

        private int indexRoot = -1;
        public int IndexRoot { get { return indexRoot; } set { indexRoot = value; } }



        public static void setDate0(DateTime date) { Date0 = date; }
        private static DateTime Date0;
        private timeSpan ts;
        public timeSpan Ts
        {
            get
            {
                if (ts == default(timeSpan))
                {
                    if (Date0 == DateTime.MinValue)
                        throw new ApplicationException("Date0 pas initialisée");
                    ts = new timeSpan(Pub2 - Date0);
                }
                return ts;
            }
        }

        public void hibernate(FileValue fv, string dirH)
        {
            bgClass.add(new BgTaskHibernate(this, fv, dirH));
        }

        protected class BgTaskHibernate : BgTaskDeleteTrackTrack
        {
            private TrackInfoItunes track;
            private FileValue fv;
            string dirH;
            public BgTaskHibernate(TrackInfoItunes track, FileValue fv, string dirH)
                : base(track, string.Format("Hibernate {0}", track))
            {
                this.track = track;
                this.fv = fv;
                this.dirH = dirH;
            }

            public override void exec()
            {
                base.exec();
                if (!success)
                    return;

                if (!string.IsNullOrEmpty(dirH))
                {
                    var dest = misc.copyTree(fv, dirH + track.ShortName, false);
                    track.HibernateLocation = dest;

                    try
                    {
                        File.Delete(track.Location);
                        fv.delete();
                    }
                    catch (Exception ex)
                    {
                        misc.log(ex);
                    }
                }
            }
        }


        private class BgTaskImport : pdb.ordo.BgTaskBase
        {
            TrackInfoItunes info;
            public BgTaskImport(TrackInfoItunes info)
                : base(string.Format("import {0}", info.Location))
            {
                this.info = info;
            }

            public override void exec()
            {
                try
                {
                    var _track = info.Track;
                    if (_track == null)
                    {
                        success = false;
                        // Thread.Sleep(1000 * Try); 
                        return;
                    }

                    info.import(_track);
                    success = true;
                }
                catch (Exception e)
                { log.log("NIET " + e.Message); }

                if (!success && info.track != null)
                {
                    info.invalidationTrack();
                    //Thread.Sleep(1000 * Try); 
                }

            }
        }

    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;

namespace pdb.podcast.Albums
{

    public class PodcastInAlbumComparer : IComparer<TrackInfoItunes>
    {
        public virtual int Compare(TrackInfoItunes x, TrackInfoItunes y)
        {
            int cmp = x.Pub.Date.CompareTo(y.Pub.Date);
            if (cmp != 0) return cmp;

            return x.Duree.Value.CompareTo(y.Duree.Value);
        }
    }

    class AlbumConsolid
    {
        private static Logger log = Logger.getLogger("doublons");
        private static IComparer<TrackInfoItunes> cmp = new PodcastInAlbumComparer();
        public void consolid(TrackAlbum album)
        {
            if (!album.CheckDoublon)
                return;

            if (album.Album == "2000 ANS D'HISTOIRE")
                return;

            var list = album.List;
            list.Sort(cmp);

            Dictionary<DateTime, List<TrackInfoItunes>> dict = new Dictionary<DateTime, List<TrackInfoItunes>>();

            TrackInfoItunes last = null;
            foreach (TrackInfoItunes t in list)
            {
                if (t.Pub.Date == t.Pub && t.Pub.DayOfYear == 1)
                    continue;
                var dt = t.Pub.Date;

                if (last != null && dt == last.Pub.Date && (int)t.Duree.TotalMinute == (int)last.Duree.TotalMinute)
                {
                    if (t.Pub == DateTime.MinValue)
                        continue;
                    if (last.LogicalName != t.LogicalName)
                    {
                        continue;
                    }


                    if (last.Pub != t.Pub)
                    {
                        if (last.Pub.Date == last.Pub || t.Pub.Date == t.Pub)
                        {
                        }
                        else
                            continue;
                    }
                    if (last.Duree != t.Duree)
                    {
                    }

                    if (!dict.ContainsKey(dt))
                    {
                        dict.Add(dt, new List<TrackInfoItunes>());
                    }
                    var subList = dict[dt];
                    if (!subList.Contains(last))
                        subList.Add(last);
                    subList.Add(t);

                }

                last = t;
            }

            foreach (KeyValuePair<DateTime, List<TrackInfoItunes>> kvp in dict)
            {
                log.log(string.Format("{0} {1}", album.Album, kvp.Key.ToString()));
                foreach (TrackInfoItunes t in kvp.Value)
                {
                    log.log("{0} {1}", t.Duree.Value, t.Location);
                }
            }
        }
    }
}

