    {
        rare = 1,
        courant = 2,
        actu = 4,
        info = 8
    }


    public class TrackAlbum : pdb.it.Albums.TrackAlbumBase, ISelectable
    {
        private albumSpec specif;
        private List<TrackInfoItunes> list = new List<TrackInfoItunes>();
        public TrackAlbum(string album)
            : base(album)
        {
            checkPub = Conf.Albums.checkPub;
            checkDoublon = Conf.Albums.checkDoublon; 
            foreach (AlbumItem _album in Conf.Albums.Items)
            {
                foreach (string alb in _album.name)
                {
                    if (alb == this.Album)
                    {
                        checkPub = _album.checkPub;
                        checkDoublon = _album.checkDoublon;
                        return; 
                    }
                }
            }

        }

