using System;
using System.Xml;

namespace pdb.util
{
    /// <summary>
    /// affecte une chaîne
    /// </summary>
    /// <param name="value"></param>
    public delegate void sets(string value);
    /// <summary>
    /// Affecte un double
    /// </summary>
    /// <param name="value"></param>
    public delegate void setd(double value);
    /// <summary>
    /// Affecte un décimal
    /// </summary>
    /// <param name="value"></param>
    public delegate void setdec(decimal value);
    /// <summary>
    /// Affecte un entier
    /// </summary>
    /// <param name="value"></param>
    public delegate void seti(int value);
    /// <summary>
    /// Affecte un entier nullable
    /// </summary>
    /// <param name="value"></param>
    public delegate void setin(int? value);
    /// <summary>
    /// Affecte un booléen
    /// </summary>
    /// <param name="value"></param>
    public delegate void setb(bool value);
    /// <summary>
    /// Affecte un booléen nullable
    /// </summary>
    /// <param name="value"></param>
    public delegate void setbn(bool? value);
    /// <summary>
    /// Affecte une date
    /// </summary>
    /// <param name="value"></param>
    public delegate void setDate(DateTime value);

    [Serializable]
    public enum dbMode
    {
        full,
        diff
    }

    [Serializable]
    public class TuningBase : ICloneable
    {
        public const string DIR_CONF = @"..\conf\";

        [NonSerialized]
        protected XmlElement parent;
        [NonSerialized]
        protected XmlElement child;

        public TuningBase()
            : this(null, null)
        {
        }

        public TuningBase(XmlElement node) : this(null, node) { }

        public TuningBase(XmlElement parent, XmlElement child)
        {
            this.parent = parent;
            this.child = child;
        }
        protected XmlWriter w;
        public virtual void save(XmlWriter w) { this.w = w; }

        protected void writeStart(string name)
        {
            w.WriteStartElement(name);
        }
        protected void writeEnd()
        {
            w.WriteEndElement();
        }
        protected void write(string name, object value)
        {
            string val = "";
            if (value != null)
            {
                if (value is bool || value is bool?)
                    val = (bool)value ? "true" : "false";
                else
                    val = value.ToString();
            }
            w.WriteAttributeString(name, val);
        }


        /// <summary>
        /// Charge un double
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setd"></param>
        protected void buildd(string name, setd setd)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                setd(Convert.ToDouble(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                setd(Convert.ToDouble(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                setd(Convert.ToDouble(elt.InnerText));
            elt = child[name];
            if (elt != null)
                setd(Convert.ToDouble(elt.InnerText));
        }
        /// <summary>
        /// Charge un décimal
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setd"></param>
        protected void builddec(string name, setdec setd)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                setd(Convert.ToDecimal(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                setd(Convert.ToDecimal(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                setd(Convert.ToDecimal(elt.InnerText));
            elt = child[name];
            if (elt != null)
                setd(Convert.ToDecimal(elt.InnerText));
        }
        /// <summary>
        /// Charge un entier
        /// </summary>
        /// <param name="name"></param>
        /// <param name="seti"></param>
        protected void buildi(string name, seti seti)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                seti(Convert.ToInt32(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                seti(Convert.ToInt32(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                seti(Convert.ToInt32(elt.InnerText));
            elt = child[name];
            if (elt != null)
                seti(Convert.ToInt32(elt.InnerText));
        }
        /// <summary>
        /// Charge un entier nullable
        /// </summary>
        /// <param name="name"></param>
        /// <param name="seti"></param>
        protected void buildin(string name, setin seti)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                seti(Convert.ToInt32(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                seti(Convert.ToInt32(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                seti(Convert.ToInt32(elt.InnerText));
            elt = child[name];
            if (elt != null)
                seti(Convert.ToInt32(elt.InnerText));
        }
        /// <summary>
        /// Charge une chaîne
        /// </summary>
        /// <param name="name"></param>
        /// <param name="sets"></param>
        protected void builds(string name, sets sets)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                sets(att.InnerText);
            att = child.Attributes[name];
            if (att != null)
                sets(att.InnerText);

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                sets(elt.InnerText);
            elt = child[name];
            if (elt != null)
                sets(elt.InnerText);
        }
        /// <summary>
        /// Charge un booléen
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setb"></param>
        protected void buildb(string name, setb setb)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                setb(readBool(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                setb(readBool(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                setb(readBool(elt.InnerText)); 
            elt = child[name];
            if (elt != null)
                setb(readBool(elt.InnerText)); 
        }
        /// <summary>
        /// Charge un booléen nullable
        /// </summary>
        /// <param name="str"></param>
        /// <param name="setb"></param>
        private void _buildbn(string str, setbn setb)
        {            
           setb(readBoolNullable(str)); 
        }

        private bool? readBoolNullable(string str)
        {
            switch (str)
            {
                case "y":
                case "Y":
                case "o":
                case "O": 
                case "1": 
                case "true":return true;
                case "n":
                case "N":
                case "0":
                case "false": return false;
                default: return null; 
            }
        }

        private bool readBool(string str)
        {
            return readBoolNullable(str).Value; 
        }
        /// <summary>
        /// Construction d'un bool nullable
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setb"></param>
        protected void buildbn(string name, setbn setb)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                _buildbn(att.InnerText, setb);

            att = child.Attributes[name];
            if (att != null)
                _buildbn(att.InnerText, setb);


            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                _buildbn(elt.InnerText, setb);
            elt = child[name];
            if (elt != null)
                _buildbn(elt.InnerText, setb);
        }
        /// <summary>
        /// Charge une date
        /// </summary>
        /// <param name="name"></param>
        /// <param name="setDate"></param>
        protected void buildDate(string name, setDate setDate)
        {
            XmlAttribute att = null;
            if (parent != null)
                att = parent.Attributes[name];
            if (att != null)
                setDate(Convert.ToDateTime(att.InnerText));
            att = child.Attributes[name];
            if (att != null)
                setDate(Convert.ToDateTime(att.InnerText));

            XmlElement elt = null;
            if (parent != null)
                elt = parent[name];
            if (elt != null)
                setDate(Convert.ToDateTime(elt.InnerText));
            elt = child[name];
            if (elt != null)
                setDate(Convert.ToDateTime(elt.InnerText));
        }

        public virtual object Clone()
        {
            return this.MemberwiseClone(); 
        }
    }
}
