    public enum pieceExtendedMode
    {
        main,
        min,
        combine
    }

    public class PieceExtended : IPieceAlbum, IValue
    {

        private Piece piece;
        private PieceSupInfo main;
        private PieceSupInfo min;
        private PieceSupInfo combine;
        private static pieceExtendedMode _mode;
        private bool empty;
        ///// <summary>
        ///// Permet d'utiliser le classement Compact. 
        ///// </summary>
        //private bool useCompact; 
        //// private bool effectMain;


        public PieceExtended(Piece piece)
        {
            this.piece = piece;
            //   this.useCompact = piece.compact(); 
            //var cl = piece.RapportClassement(true);
            //if (cl == 0m)
            if (!piece.ClassmentPositive())
                empty = true;
            else
            {
                this.main = new PieceSupInfo(this);
                this.min = new PieceSupInfo(this);
            }
        }

        public void Combine()
        {
            if (empty)
                return;

            this.combine = new PieceSupInfo(this);
            var coeff = main.Coeff * min.Coeff;
            if (coeff > 1)
                combine.Coeff = coeff;

        }

        public decimal VirtualClassement { get { if (empty) return 0; return main.Value; } }
        public decimal VirtualClassement2 { get { if (empty) return 0; return min.Value; } }
        public decimal Cumul { get { if (empty) return 0; return combine.Value; } }
        public bool UseCompact { get { return piece.UseCompact; } set { piece.UseCompact = value; } }


        public bool getCauseUp(bool? natureCause)
        {
            if (empty)
                return false;
            if (true == natureCause)
                return min.Coeff > 1;
            if (false == natureCause)
                return main.Coeff > 1;
            return min.Coeff > 1 || main.Coeff > 1;
        }

        public bool? getSimpleCause()
        {
            if (empty)
                return null;

            var coeff1 = main.Coeff;
            var coeff2 = min.Coeff;
            if (coeff2 > coeff1)
                return true;
            else if (coeff1 > coeff2)
                return false;
            return null;
        }

        public decimal CoeffVirtuel { get { if (empty) return 1m; return main.Coeff; } }
        public decimal CoeffVirtuel2 { get { if (empty) return 1m; return min.Coeff; } }

        public Piece Piece { get { return piece; } }
        public bool Enabled { get { return piece.Enabled; } }
        public bool Empty { get { return empty; } }

        public bool LastClassementRecent { get { if (empty) return false; return piece.LastClassementRecent; } }
        public decimal RapportClassement { get { if (empty) return 0m; return piece.RapportClassement(true); } }
        public Classement Classement { get { return piece.getClassement(true); } }
        public string Hr { get { return RapportClassement.hr(); } }




        public void setCoeff(decimal coeff, bool main)
        {
            if (empty)
                return;

            if (main)
                this.main.Coeff = coeff;//   etCoeff(coeff, value0);
            else
                this.min.Coeff = coeff; // setCoeff(coeff, value0);
        }

        //public void setValue(decimal value, bool main)
        //{

        //    if (empty)
        //        return;
        //    if (main)
        //        this.main.Value = value;
        //    else
        //        this.min.Value = value;
        //}
#if GEN2
        public void finalyse()
        {

            if (empty)
                return;
            this.piece.setClassementVirtuel(this.main.Value);
            this.piece.setClassementVirtuel2(this.min.Value);

        }
#endif

        public override string ToString()
        {
            return string.Format("{0} enabled={1} coeff1={2} coeff2={3} main={4} min={5} p={6}", Hr, Enabled, CoeffVirtuel, CoeffVirtuel2, main.Hr, min.Hr, Piece);
        }


        internal PieceSupInfo Current
        {
            get
            {
                switch (_mode)
                {
                    case pieceExtendedMode.main: return main;
                    case pieceExtendedMode.min: return min;
                    case pieceExtendedMode.combine: return combine;
                    default: throw new NotImplementedException();
                }

            }
        }

        public decimal Value
        {
            get
            {
                if (empty)
                    return 0m;
                return Current.Value;
            }

            set
            {
                if (empty)
                    throw new ArgumentException("on ne peut affecter une valeur à un empty");
                if (value <= 0)
                    empty = true;
                else
                    Current.Value = value;
            }
        }

        public decimal Coeff
        {
            set
            {
                if (empty)
                    return;
                Current.Coeff = value;
            }
        }

        public static pieceExtendedMode Mode
        {
            get { return _mode; }
            set { _mode = value; }
        }
        //  public bool EffectMain { get { return effectMain; } set { effectMain = value; } }
    }

    class PieceSupInfo
    {
        private PieceExtended piece;

        // private  decimal V0;
        // private decimal value;
        private decimal coeff;

        //private decimal V0 { get { return piece.RapportClassement(true); } }

        public PieceSupInfo(PieceExtended piece)
        {
            this.piece = piece;
            //   this.value = piece.RapportClassement(true);
            this.coeff = 1;
            // this.V0 = value; 
        }


        public decimal Coeff
        {
            get
            {
                return coeff;
                //if (value <= 0) return 1m;
                ////if (V0 / value > 1.1m)
                ////    Debugger.Launch();
                //return piece.RapportClassement(true) / value;
            }
            set
            {
                if (value > 1.1m)
                {
                }
                this.coeff = value;
                // this.value = piece.RapportClassement(true) / coeff;
            }

        }
        //public decimal getCoeff(decimal value0)
        //{
        //    if (value <= 0) return 1m;
        //}

        //public void setCoeff(decimal coeff, decimal value0)
        //{
        //    if (value0 > 0)
        //        this.value = value0 / coeff;

        //}

        public decimal Value
        {
            get
            {
                if (coeff <= 0)
                    return 0;
                return piece.RapportClassement / coeff;
            }
            set
            {
                if (value <= 0)
                {
                    coeff = 0;
                }
                else
                    coeff = piece.RapportClassement / value;
                if (coeff > 1.1m)
                {
                }
            }
        }

        public string Hr { get { return Value.hr(); } }
    }
}

