            view = CollectionViewSource.GetDefaultView(model);
            using (var w = view.DeferRefresh())
            {
                // items.refresh();

                base.DataContext = model;
                int i = 0;
                if (pl.isSubList("gen"))
                {
                }
                else
                {
                    //foreach (TrackListViewModel track in items)
