    class LinkFusion : CommandeBase
    {

        public LinkFusion()
        {
            LinkRegister.sthChange += new System.EventHandler(LinkRegister_sthChange);
        }

        void LinkRegister_sthChange(object sender, System.EventArgs e)
        {
            OnPropertyChanged("Header"); 
        }
        public override bool CanExecute(object parameter)
        {
            return LinkRegister.Father != null; // || LinkRegister.Child != null; 
        }

        public override void Execute(object parameter)
        {
            var track = TrackListViewModel.HumanSelected;
            // LinkRegister.Child = track;
            OnPropertyChanged("Header");

            var father = LinkRegister.Father;
            if (father == null)
                return; 
            if (track == null)
                return;
            bool? byAlbum = App.Instance.TriChoix(string.Format( "fusionner l'abum entier père {0} \r\n fils {1} ?",father, track), "Fusion");

            if (byAlbum == null)
                return;
            if (false == byAlbum)
            {
                father.fusion(track);
            }
            else
            {
                var albumFather = App.gen.getTracksAlbumByNumber(father.Track);
                var albumSon = App.gen.getTracksAlbumByNumber(track.Track);
                int count = albumFather.Count;
                int count2 = albumSon.Count;
                int nb = 0;
                int tn = 1; 

                int maxtn=0; 
                foreach (var t in albumFather)
                {
                    var _tn = t.TrackNumber;
                    if (_tn> maxtn)
                    {
                        maxtn = _tn; 
                    }
                }
                 foreach (var t in albumSon)
                {
                    var _tn = t.TrackNumber;
                    if (_tn> maxtn)
                    {
                        maxtn = _tn; 
                    }
                }

                while(tn<=maxtn)
                {
                    try
                    {
                        var f = albumFather.Find(t => t.TrackNumber == tn);
                        var s = albumSon.Find(t => t.TrackNumber == tn);
                        if (f != null && s != null)
                        {
                            bool? go = App.Instance.TriChoix("fusion de " + f + " (track n°" + f.TrackNumber + ") et " + Environment.NewLine + s + " (track n°" + s.TrackNumber + ")?", "fusion track " + f.Track);
                            if (go == null)
                                break;
                            if (false == go)
                                continue;
                            App.Db.List.fusion(f.Track as CPiece, s.Track as CPiece);
                            nb++; 

                        }
                    }
                    finally
                    {
                        tn++; 
                    }
                }

                App.Instance.InfoIHm(nb + " éléments fusionnés", "fusion"); 
            }

            App.go(); 
        }

        public override string Header
        {
            get
            {
                var father = LinkRegister.Father;
                if (father == null)
                    return "fusionner avec ...";
                return "Fusionner avec " + father.ToString(); 
            }
        }
    }
}

using System;
using System.Collections.Generic;
using System.IO;
using System.Threading;
