using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.db.obj;

namespace pdb.db.piece.virtuel
{
    public class VirtualPiece : CPiece
    {
        private static int vid = -1; 
        private CPiece piece;
        private string _album; 
        public VirtualPiece(CPiece piece)
        {
            _virtual = true; 
            this.piece = piece;
            m_id = vid; 
        }

        public VirtualPiece(CPiece piece, string _album)
            : this(piece)
        {
            this._album = _album; 
        }

        public override string Album
        {
            get
            {
                if (_album == null)
                return piece.Album;
                return _album; 
            }
            set
            {
                if (_album == null)
                    piece.Album = value ;

            }
        }

        public override string Artist
        {
            get
            {
                return base.Artist;
            }
            set
            {

            }
        }


    }
}

using System;
using System.Collections.Generic;

namespace pdb.obj
{

    /// <summary>
    /// définit chemin d'un fichier
    /// </summary>
    public interface IFile
    {
        String Location { get; }
        int PieceId { get; }
    }

    /// <summary>
    /// carte d'identité d'un morceau
    /// </summary>
    public interface ITrackReference
    {
        String Artist { get; set; }
        String Album { get; set; }
        String Name { get; set; }
        int TrackNumber { get; set; }
    }

    // définition non ambigue d'un morceau
    public interface ITrackIdentity : IFile, ITrackReference
    {

        int PieceParentId { get; set; }
        TrackIdentity Key { get; }
    }


    /// <summary>
    /// infos de base du morceau :métafonnées de la piste
    /// </summary> 
    public interface ITrackMetaData : ITrackIdentity
    {

        int Rating { get; set; }
        int Year { get; set; }
        String Comment { get; set; }
        bool Enabled { get; set; }
        bool isPodcast { get; }
        DateTime PlayedDate { get; }
        TimeSpan Duration { get; set; }
        string ArtWork { get; set; }
        string Grouping { get; }
        void setGrouping(string value,bool prior);
        bool FirstClass { get; set; }
#if VOL
        int Volume { get; set; }
#endif 
    }

    /// <summary>
    /// Supplément d'infos fournies par Db. liste de dates
    /// </summary>
    public interface ITrackPlayedDates
    {
        List<DateTime> Dates { get; }
        List<DateTime> DatesClassement { get; }
        String ClassEvol { get; }
    }

    /// <summary>
    /// infos max récupérées de iTunes: 
