        private class TrackComparer : IComparer<TrackListViewModel>

        {
            public int Compare(TrackListViewModel x, TrackListViewModel y)
            {
                var xx = x.PieceId;
                var yy = y.PieceId;
                if (xx > 0)
                {
                    if (yy > 0)
                        return xx.CompareTo(yy);
                    else
                        return -1;
                }
                else
                {
                    if (yy < 0)
                        return xx.CompareTo(yy);
                    else
                        return -1;
                }

            }


        }
